import { errorHandler } from '../../middleware/errorHandler.js';
import { validateBody } from '../../middleware/validation.js';
import { CognitoService } from '../../services/cognitoService.js';
import { RefreshTokenRequest } from '@buy-locals/shared';
import { refreshTokenSchema } from '../../schemas/authSchemas.js';

validateEnvironment();

const app = express();
const cognitoService = new CognitoService();

app.post('/auth/refresh', validateBody(refreshTokenSchema), async (req, res, next) => {
  try {
    const { refreshToken }: RefreshTokenRequest = req.body;

    // Refresh tokens with AWS Cognito
    const authResult = await cognitoService.refreshToken(refreshToken);

    // The new access token will contain updated user info
    // For now, we'll return the new tokens without additional user data
    // In a full implementation, we might decode the ID token to get user info

    res.json({
      token: authResult.accessToken,
      idToken: authResult.idToken,
    });
  } catch (error) {
    console.error('Token refresh error:', error);

    // Handle specific Cognito errors
    if (error instanceof Error) {
      if (
        error.message.includes('NotAuthorizedException') ||
        error.message.includes('Invalid refresh token')
      ) {
        return res.status(401).json({ error: 'Invalid refresh token' });
      }
      if (error.message.includes('TokenExpiredException')) {
        return res.status(401).json({ error: 'Refresh token expired. Please login again.' });
      }
    }

    next(error);
  }
});

app.use(errorHandler);

export const handler: APIGatewayProxyHandler = serverless(app);

import { APIGatewayProxyHandler } from 'aws-lambda';
import serverless from 'serverless-http';
import express from 'express';
import cors from 'cors';
import helmet from 'helmet';
import { v4 as uuidv4 } from 'uuid';
import { pool } from '../../config/database.js';
import { config, validateEnvironment } from '../../config/environment.js';
import { errorHandler } from '../../middleware/errorHandler.js';
import { validateBody } from '../../middleware/validation.js';
import { CognitoService } from '../../services/cognitoService.js';
import { CreateUserRequest } from '@buy-locals/shared';
import { registerSchema } from '../../schemas/authSchemas.js';
import { registrationRateLimit } from '../../middleware/rateLimiting.js';
import { auditLogger, sanitizeInput, securityHeaders } from '../../middleware/security.js';

validateEnvironment();

const app = express();
const cognitoService = new CognitoService();

app.use(helmet());
app.use(cors({ origin: config.corsOrigin }));
app.use(express.json());
app.use(securityHeaders);
app.use(sanitizeInput);
app.use(auditLogger);

app.post(
  '/auth/register',
  registrationRateLimit,
  validateBody(registerSchema),
  async (req, res, next) => {
    try {
      const userData: CreateUserRequest = req.body;

      // Check if user already exists in database
      const existingUser = await pool.query('SELECT id FROM users WHERE email = $1', [
        userData.email,
      ]);

      if (existingUser.rows.length > 0) {
        return res.status(409).json({ error: 'User already exists' });
      }

      // Register user in AWS Cognito
      await cognitoService.registerUser(userData);

      // Create user record in database with JSONB profile
      const dbUserId = uuidv4();
      const profile = {
        firstName: userData.firstName,
        lastName: userData.lastName,
        phone: userData.phone || undefined,
      };

      const result = await pool.query(
        `INSERT INTO users (id, email, password_hash, role, profile, is_email_verified, created_at, updated_at)
       VALUES ($1, $2, $3, $4, $5, $6, NOW(), NOW())
       RETURNING id, email, role, profile, is_email_verified, created_at, updated_at`,
        [dbUserId, userData.email, '', userData.role || 'consumer', JSON.stringify(profile), false]
      );

      const user = result.rows[0];

      // Authenticate the newly created user to get tokens
      const authResult = await cognitoService.loginUser(userData.email, userData.password);

      // Return success response with tokens
      res.status(201).json({
        token: authResult.accessToken,
        refreshToken: authResult.refreshToken,
        user: {
          id: user.id,
          email: user.email,
          role: user.role,
          profile: user.profile,
          isEmailVerified: user.is_email_verified,
          createdAt: user.created_at,
          updatedAt: user.updated_at,
        },
      });
    } catch (error) {
      console.error('Registration error:', error);

      // Handle specific Cognito errors
      if (error instanceof Error) {
        if (error.message.includes('UsernameExistsException')) {
          return res.status(409).json({ error: 'User already exists' });
        }
        if (error.message.includes('InvalidPasswordException')) {
          return res.status(400).json({ error: 'Password does not meet requirements' });
        }
      }

      next(error);
    }
  }
);

app.use(errorHandler);

export const handler: APIGatewayProxyHandler = serverless(app);

import { APIGatewayProxyHandler } from 'aws-lambda';
import serverless from 'serverless-http';
import express from 'express';
import cors from 'cors';
import helmet from 'helmet';
import { config, validateEnvironment } from '../../config/environment.js';
import { errorHandler } from '../../middleware/errorHandler.js';
import { validateBody } from '../../middleware/validation.js';
import { CognitoService } from '../../services/cognitoService.js';
import { ResetPasswordRequest } from '@buy-locals/shared';
import { resetPasswordSchema } from '../../schemas/authSchemas.js';

validateEnvironment();

const app = express();
const cognitoService = new CognitoService();

app.use(helmet());
app.use(cors({ origin: config.corsOrigin }));
app.use(express.json());

app.post('/auth/reset-password', validateBody(resetPasswordSchema), async (req, res, next) => {
  try {
    const { newPassword }: ResetPasswordRequest = req.body;

    // The token from Cognito forgot password flow contains both the username and confirmation code
    // For this implementation, we expect the client to pass email and code separately
    // In a production setup, you might encode this information in the token

    // For now, we need to extract email from the request or token
    // This is a simplified implementation - in production, you'd parse the reset token
    const { email, confirmationCode } = req.body; // Additional fields expected

    if (!email || !confirmationCode) {
      return res.status(400).json({
        error: 'Email and confirmation code are required',
      });
    }

    // Confirm password reset with AWS Cognito
    await cognitoService.confirmForgotPassword(email, confirmationCode, newPassword);

    res.json({
      message: 'Password has been successfully reset. You can now log in with your new password.',
    });
  } catch (error) {
    console.error('Reset password error:', error);

    // Handle specific Cognito errors
    if (error instanceof Error) {
      if (error.message.includes('ExpiredCodeException')) {
        return res.status(400).json({
          error: 'Reset code has expired. Please request a new password reset.',
        });
      }
      if (error.message.includes('InvalidParameterException')) {
        return res.status(400).json({
          error: 'Invalid reset code. Please check and try again.',
        });
      }
      if (error.message.includes('CodeMismatchException')) {
        return res.status(400).json({
          error: 'Invalid reset code. Please check and try again.',
        });
      }
      if (error.message.includes('UserNotFoundException')) {
        return res.status(404).json({
          error: 'User not found.',
        });
      }
      if (error.message.includes('InvalidPasswordException')) {
        return res.status(400).json({
          error: 'Password does not meet requirements. Please choose a stronger password.',
        });
      }
      if (error.message.includes('TooManyRequestsException')) {
        return res.status(429).json({
          error: 'Too many reset attempts. Please try again later.',
        });
      }
    }

    next(error);
  }
});

app.use(errorHandler);

export const handler: APIGatewayProxyHandler = serverless(app);

