// GET user profile
app.get('/user/profile', authenticateCognito, async (req: CognitoAuthenticatedRequest, res, next) => {
  try {
    const userId = req.user?.id;

    if (!userId) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    // Get user data from database
    const result = await pool.query(
      'SELECT id, email, role, profile, is_email_verified, created_at, updated_at, last_login_at FROM users WHERE id = $1',
      [userId]
    );

    if (result.rows.length === 0) {
      return res.status(404).json({ error: 'User not found' });
    }

    const user = result.rows[0];

    res.json({
      user: {
        id: user.id,
        email: user.email,
        role: user.role,
        profile: user.profile,
        isEmailVerified: user.is_email_verified,
        createdAt: user.created_at,
        updatedAt: user.updated_at,
        lastLoginAt: user.last_login_at,
      },
    });
  } catch (error) {
    console.error('Get profile error:', error);
    next(error);
  }
});

// PUT update user profile
app.put('/user/profile', authenticateCognito, validateBody(updateProfileSchema), async (req: CognitoAuthenticatedRequest, res, next) => {
  try {
    const userId = req.user?.id;
    const updates: UpdateProfileRequest = req.body;

    if (!userId) {
      return res.status(401).json({ error: 'Authentication required' });
    }

    // Get current user data
    const currentResult = await pool.query(
      'SELECT profile FROM users WHERE id = $1',
      [userId]
    );

    if (currentResult.rows.length === 0) {
      return res.status(404).json({ error: 'User not found' });
    }

    const currentProfile = currentResult.rows[0].profile || {};

    // Merge updates with current profile
    const updatedProfile = {
      ...currentProfile,
      ...(updates.firstName && { firstName: updates.firstName }),
      ...(updates.lastName && { lastName: updates.lastName }),
      ...(updates.phone && { phone: updates.phone }),
      ...(updates.locationPreferences && { locationPreferences: updates.locationPreferences }),
    };

    // Update profile in database
    const updateResult = await pool.query(
      'UPDATE users SET profile = $1, updated_at = NOW() WHERE id = $2 RETURNING id, email, role, profile, is_email_verified, created_at, updated_at, last_login_at',
      [JSON.stringify(updatedProfile), userId]
    );

    const user = updateResult.rows[0];

    // Also update user attributes in Cognito if name or phone changed
    if (updates.firstName || updates.lastName || updates.phone) {
      try {
        await cognitoService.updateUserProfile(req.user?.email || '', {
          firstName: updates.firstName,
          lastName: updates.lastName,
          phone: updates.phone,
        });
      } catch (cognitoError) {
        console.error('Failed to update Cognito profile:', cognitoError);
        // Don't fail the request if Cognito update fails
      }
    }

    res.json({
      message: 'Profile updated successfully',
      user: {
        id: user.id,
        email: user.email,
        role: user.role,
        profile: user.profile,
        isEmailVerified: user.is_email_verified,
        createdAt: user.created_at,
        updatedAt: user.updated_at,
        lastLoginAt: user.last_login_at,
      },
    });
  } catch (error) {
    console.error('Update profile error:', error);
    next(error);
  }
});

app.use(errorHandler);

export const handler: APIGatewayProxyHandler = serverless(app);

import { NextFunction, Request, Response } from 'express';
import { pool } from '../config/database.js';
import { errorResponse, successResponse } from '../utils/responseUtils.js';

export const healthHandler = async (req: Request, res: Response, _next: NextFunction) => {
  try {
    // Check database connection
    const dbCheck = await pool.query('SELECT 1');
    const isDbHealthy = dbCheck.rows.length > 0;

    // Check Redis connection if configured
    let isRedisHealthy = true;
    try {
      const redis = await import('../config/redis.js');
      if (redis.redisClient) {
        await redis.redisClient.ping();
      }
    } catch (redisError) {
      isRedisHealthy = false;
    }

    const healthData = {
      status: 'healthy',
      timestamp: new Date().toISOString(),
      environment: process.env.NODE_ENV || 'development',
      stage: process.env.STAGE || 'dev',
      version: process.env.npm_package_version || '1.0.0',
      uptime: process.uptime(),
      services: {
        database: isDbHealthy ? 'healthy' : 'unhealthy',
        redis: isRedisHealthy ? 'healthy' : 'unhealthy',
      },
    };

    // Return 503 if critical services are down
    if (!isDbHealthy) {
      return errorResponse(res, 503, 'Service unavailable - database connection failed');
    }

    return successResponse(res, 200, healthData, 'Buy Locals API is healthy');
  } catch (error) {
    console.error('Health check failed:', error);
    return errorResponse(res, 503, 'Service unavailable');
  }
};

import { NextFunction, Request, Response } from 'express';
import jwt from 'jsonwebtoken';
import { config } from '../config/environment.js';
import { errorResponse } from '../utils/responseUtils.js';
import { UserService } from '../services/userService.js';

// Extend Express Request interface to include user
declare module 'express-serve-static-core' {
  interface Request {
    user?: {
      id: string;
      email: string;
      role: string;
    };
  }
}

export interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    email: string;
    role: string;
  };
}

const userService = new UserService();

/**
 * Middleware to authenticate JWT tokens
 */
export const authMiddleware = async (req: Request, res: Response, next: NextFunction) => {
  try {
    const authHeader = req.headers['authorization'];
    const token = authHeader && authHeader.split(' ')[1];

    if (!token) {
      return errorResponse(res, 401, 'Access token required');
    }

    // Verify JWT token
    const decoded = jwt.verify(token, config.jwtSecret) as any;

    // Validate that user still exists and is active
    const user = await userService.getUserProfile(decoded.id);
    if (!user) {
      return errorResponse(res, 401, 'User not found or inactive');
    }

    // Attach user info to request
    req.user = {
      id: user.id,
      email: user.email,
      role: user.role,
    };

    next();
  } catch (error) {
    if (error instanceof jwt.JsonWebTokenError) {
      return errorResponse(res, 403, 'Invalid or expired token');
    }
    next(error);
  }
};

/**
 * Middleware to require specific roles
 */
export const requireRole = (allowedRoles: string[]) => {
  return (req: Request, res: Response, next: NextFunction) => {
    if (!req.user) {
      return errorResponse(res, 401, 'Authentication required');
    }

    if (!allowedRoles.includes(req.user.role)) {
      return errorResponse(res, 403, 'Insufficient permissions');
    }

    next();
  };
};

/**
 * Middleware to require business owner role or admin
 */
export const requireBusinessOwner = (req: Request, res: Response, next: NextFunction) => {
  return requireRole(['business_owner', 'admin'])(req, res, next);
};

/**
 * Middleware to require admin role
 */
export const requireAdmin = (req: Request, res: Response, next: NextFunction) => {
  return requireRole(['admin'])(req, res, next);
};

/**
 * Generate JWT token for user
 */
export const generateToken = (user: { id: string; email: string; role: string }): string => {
  return jwt.sign(
    {
      id: user.id,
      email: user.email,
      role: user.role,
    },
    config.jwtSecret,
    {
      expiresIn: '24h',
      issuer: 'buy-locals-api',
    }
  );
};

/**
 * Generate refresh token
 */
export const generateRefreshToken = (userId: string): string => {
  return jwt.sign({ userId }, `${config.jwtSecret}_refresh`, {
    expiresIn: '7d',
    issuer: 'buy-locals-api',
  });
};

