/**
 * Audit logging middleware
 */
export const auditLogger = (req: Request, res: Response, next: NextFunction) => {
  const startTime = Date.now();

  // Generate correlation ID for request tracking
  const correlationId = crypto.randomUUID();
  req.headers['x-correlation-id'] = correlationId;
  res.setHeader('X-Correlation-ID', correlationId);

  // Log request details (without sensitive data)
  const logData = {
    correlationId,
    timestamp: new Date().toISOString(),
    method: req.method,
    url: req.url,
    userAgent: req.get('User-Agent'),
    ip: req.ip,
    // Don't log request body for security - it might contain passwords
  };

  console.log('AUTH_REQUEST:', JSON.stringify(logData));

  // Override res.json to log response
  const originalJson = res.json;
  res.json = function(body: any) {
    const duration = Date.now() - startTime;

    // Log response (without sensitive data)
    const responseLog = {
      correlationId,
      timestamp: new Date().toISOString(),
      statusCode: res.statusCode,
      duration,
      // Don't log response body for security
    };

    console.log('AUTH_RESPONSE:', JSON.stringify(responseLog));
    return originalJson.call(this, body);
  };

  next();
};

/**
 * Password complexity validation
 */
export const passwordComplexity = {
  minLength: 8,
  requireUppercase: true,
  requireLowercase: true,
  requireNumbers: true,
  requireSpecialChars: false, // Made optional for better UX

  validate(password: string): {
    isValid: boolean;
    errors: string[];
  } {
    const errors: string[] = [];

    if (password.length < this.minLength) {
      errors.push(`Password must be at least ${this.minLength} characters long`);
    }

    if (this.requireUppercase && !/[A-Z]/.test(password)) {
      errors.push('Password must contain at least one uppercase letter');
    }

    if (this.requireLowercase && !/[a-z]/.test(password)) {
      errors.push('Password must contain at least one lowercase letter');
    }

    if (this.requireNumbers && !/\d/.test(password)) {
      errors.push('Password must contain at least one number');
    }

    if (this.requireSpecialChars && !/[!@#$%^&*(),.?":{}|<>]/.test(password)) {
      errors.push('Password must contain at least one special character');
    }

    // Check for common weak patterns
    const commonPatterns = [
      /123456/,
      /password/i,
      /qwerty/i,
      /abc123/i,
    ];

    for (const pattern of commonPatterns) {
      if (pattern.test(password)) {
        errors.push('Password contains common patterns and is not secure');
        break;
      }
    }

    return {
      isValid: errors.length === 0,
      errors,
    };
  }
};

/**
 * CSRF protection (for cookie-based auth if used)
 */
export const csrfProtection = (req: Request, res: Response, next: NextFunction) => {
  // Skip CSRF for safe methods
  if (['GET', 'HEAD', 'OPTIONS'].includes(req.method)) {
    return next();
  }

  // For JWT-based auth, CSRF is less of a concern
  // But we can still validate origin headers
  const origin = req.get('Origin');
  const allowedOrigins = [
    process.env.CORS_ORIGIN || 'http://localhost:3000',
    'http://localhost:3001', // Mobile dev server
  ];

  if (origin && !allowedOrigins.includes(origin)) {
    return res.status(403).json({
      error: 'Forbidden',
      message: 'Invalid origin',
    });
  }

  next();
};

import { NextFunction, Request, Response } from 'express';
import Joi from 'joi';
import { ApiError } from './errorHandler.js';

export const validateBody = (schema: Joi.ObjectSchema) => {
  return (req: Request, res: Response, next: NextFunction) => {
    const { error } = schema.validate(req.body);

    if (error) {
      const validationError: ApiError = new Error('Validation failed') as ApiError;
      validationError.statusCode = 400;
      validationError.isOperational = true;
      validationError.details = error.details.map(detail => detail.message);
      return next(validationError);
    }

    next();
  };
};

export const validateQuery = (schema: Joi.ObjectSchema) => {
  return (req: Request, res: Response, next: NextFunction) => {
    const { error } = schema.validate(req.query);

    if (error) {
      const validationError: ApiError = new Error('Validation failed') as ApiError;
      validationError.statusCode = 400;
      validationError.isOperational = true;
      validationError.details = error.details.map(detail => detail.message);
      return next(validationError);
    }

    next();
  };
};

export const validateParams = (schema: Joi.ObjectSchema) => {
  return (req: Request, res: Response, next: NextFunction) => {
    const { error } = schema.validate(req.params);

    if (error) {
      const validationError: ApiError = new Error('Validation failed') as ApiError;
      validationError.statusCode = 400;
      validationError.isOperational = true;
      validationError.details = error.details.map(detail => detail.message);
      return next(validationError);
    }

    next();
  };
};


export const validationErrorHandler = (err: any, req: Request, res: Response, next: NextFunction) => {
  // Handle Joi validation errors specifically
  if (err.isJoi) {
    const validationError: ApiError = new Error('Validation failed') as ApiError;
    validationError.statusCode = 400;
    validationError.isOperational = true;
    validationError.details = err.details.map((detail: any) => detail.message);
    return next(validationError);
  }

  next(err);
};

import { Pool, PoolClient } from 'pg';
import { pool } from '../config/database.js';

export interface QueryOptions {
  limit?: number;
  offset?: number;
  orderBy?: string;
  orderDirection?: 'ASC' | 'DESC';
}

export interface WhereClause {
  [key: string]: any;
}

export abstract class BaseRepository<T> {
  protected pool: Pool;
  protected tableName: string;

  constructor(tableName: string) {
    this.pool = pool;
    this.tableName = tableName;
  }

  /**
   * Execute a query with connection pooling and error handling
   */
  protected async query(text: string, params?: any[]): Promise<any> {
    let client: PoolClient | undefined;
    try {
      client = await this.pool.connect();
      const result = await client.query(text, params);
      return result;
    } catch (error) {
      console.error(`Database query error in ${this.tableName}:`, {
        error: error instanceof Error ? error.message : String(error),
        query: text.substring(0, 200), // Log first 200 chars of query for debugging
        table: this.tableName,
        timestamp: new Date().toISOString()
      });
      throw error;
    } finally {
      if (client) {
        client.release();
      }
    }
  }

  /**
   * Execute multiple queries in a transaction
   */
  protected async transaction(queries: Array<{ text: string; params?: any[] }>): Promise<any[]> {
    let client: PoolClient | undefined;
    try {
      client = await this.pool.connect();
      await client.query('BEGIN');

      const results = [];
      for (const query of queries) {
        const result = await client.query(query.text, query.params);
        results.push(result);
      }

      await client.query('COMMIT');
      return results;
    } catch (error) {
      if (client) {
        await client.query('ROLLBACK');
      }
      console.error(`Transaction error in ${this.tableName}:`, error);
      throw error;
    } finally {
      if (client) {
        client.release();
      }
    }
  }

  /**
   * Find record by ID
   */
  async findById(id: string): Promise<T | null> {
    const query = `SELECT * FROM ${this.tableName} WHERE id = $1`;
    const result = await this.query(query, [id]);
    return result.rows[0] || null;
  }

  /**
   * Find all records with optional filtering and pagination
   */
  async findAll(options: QueryOptions = {}): Promise<T[]> {
    let query = `SELECT * FROM ${this.tableName}`;
    const params: any[] = [];

    if (options.orderBy) {
      query += ` ORDER BY ${options.orderBy} ${options.orderDirection || 'ASC'}`;
    }

    if (options.limit) {
      params.push(options.limit);
      query += ` LIMIT $${params.length}`;
    }

    if (options.offset) {
      params.push(options.offset);
      query += ` OFFSET $${params.length}`;
    }

    const result = await this.query(query, params);
    return result.rows;
  }

  /**
   * Count total records
   */
  async count(): Promise<number> {
    const query = `SELECT COUNT(*) FROM ${this.tableName}`;
    const result = await this.query(query);
    return parseInt(result.rows[0].count);
  }

  /**
   * Create a new record
   */
  async create(data: Partial<T>): Promise<T> {
    const keys = Object.keys(data as any);
    const values = Object.values(data as any);
    const placeholders = values.map((_, index) => `$${index + 1}`);

    const query = `
      INSERT INTO ${this.tableName} (${keys.join(', ')})
      VALUES (${placeholders.join(', ')})
      RETURNING *
    `;

    const result = await this.query(query, values);
    return result.rows[0];
  }

  /**
   * Update a record by ID
   */
  async update(id: string, data: Partial<T>): Promise<T | null> {
    const keys = Object.keys(data as any);
    const values = Object.values(data as any);
    const setClause = keys.map((key, index) => `${key} = $${index + 2}`).join(', ');

    const query = `
      UPDATE ${this.tableName}
      SET ${setClause}, updated_at = CURRENT_TIMESTAMP
      WHERE id = $1
      RETURNING *
    `;

    const result = await this.query(query, [id, ...values]);
    return result.rows[0] || null;
  }

  /**
   * Delete a record by ID
   */
  async delete(id: string): Promise<boolean> {
    const query = `DELETE FROM ${this.tableName} WHERE id = $1`;
    const result = await this.query(query, [id]);
    return result.rowCount > 0;
  }

  /**
   * Find records by custom where clause
   */
  async findWhere(whereClause: WhereClause, options: QueryOptions = {}): Promise<T[]> {
    const whereKeys = Object.keys(whereClause);
    const whereValues = Object.values(whereClause);
    const whereConditions = whereKeys.map((key, index) => `${key} = $${index + 1}`);

    let query = `SELECT * FROM ${this.tableName} WHERE ${whereConditions.join(' AND ')}`;
    const params = whereValues;

    if (options.orderBy) {
      query += ` ORDER BY ${options.orderBy} ${options.orderDirection || 'ASC'}`;
    }

    if (options.limit) {
      params.push(options.limit);
      query += ` LIMIT $${params.length}`;
    }

    if (options.offset) {
      params.push(options.offset);
      query += ` OFFSET $${params.length}`;
    }

    const result = await this.query(query, params);
    return result.rows;
  }

  /**
   * Check if connection is healthy
   */
  async healthCheck(): Promise<boolean> {
    try {
      await this.query('SELECT 1');
      return true;
    } catch (error) {
      return false;
    }
  }
}

import { BaseRepository } from './BaseRepository.js';
import { Business, BusinessSearchQuery, CreateBusinessRequest } from '../types/Business.js';

export class BusinessRepository extends BaseRepository<Business> {
  constructor() {
    super('businesses');
  }

  /**
   * Create a new business
   */
  async createBusiness(ownerId: string, businessData: CreateBusinessRequest): Promise<Business> {
    const query = `
      INSERT INTO businesses (
        owner_id, name, description, location, categories, 
        hours, contact, services, is_active
      ) VALUES ($1, $2, $3, $4, $5, $6, $7, $8, $9)
      RETURNING *
    `;

    const values = [
      ownerId,
      businessData.name,
      businessData.description || null,
      JSON.stringify(businessData.location),
      businessData.categories,
      JSON.stringify(businessData.hours),
      JSON.stringify(businessData.contact),
      JSON.stringify(businessData.services || []),
      true, // Default to active
    ];

    const result = await this.query(query, values);
    return result.rows[0];
  }

  /**
   * Find businesses by owner ID
   */
  async findByOwnerId(ownerId: string): Promise<Business[]> {
    const query = 'SELECT * FROM businesses WHERE owner_id = $1 ORDER BY created_at DESC';
    const result = await this.query(query, [ownerId]);
    return result.rows;
  }

  /**
   * Search businesses with optimized PostGIS location-based filtering
   */
  async searchBusinesses(searchQuery: BusinessSearchQuery): Promise<{
    businesses: Business[];
    totalCount: number;
  }> {
    const { lat, lng, radius = 25, category, search, page = 1, limit = 10 } = searchQuery;
    const offset = (page - 1) * limit;

    // Use PostGIS if coordinates are provided, otherwise fall back to JSONB
    if (lat && lng) {
      return this.searchBusinessesPostGIS(searchQuery);
    }

    // Fallback to original JSONB-based search for non-location queries
    const whereConditions: string[] = ['is_active = true'];
    const params: any[] = [];
    let paramIndex = 1;

    // Category filter
    if (category) {
      whereConditions.push(`$${paramIndex} = ANY(categories)`);
      params.push(category);
      paramIndex++;
    }

    // Text search in name and description
    if (search) {
      whereConditions.push(`
        (name ILIKE $${paramIndex} OR description ILIKE $${paramIndex})
      `);
      params.push(`%${search}%`);
      paramIndex++;
    }

    // Main query for businesses
    const businessQuery = `
      SELECT *
      FROM businesses 
      WHERE ${whereConditions.join(' AND ')}
      ORDER BY created_at DESC
      LIMIT $${paramIndex} OFFSET $${paramIndex + 1}
    `;
    params.push(limit, offset);

    // Count query for pagination
    const countQuery = `
      SELECT COUNT(*) 
      FROM businesses 
      WHERE ${whereConditions.join(' AND ')}
    `;
    const countParams = params.slice(0, -2); // Remove limit and offset

    const [businessResult, countResult] = await Promise.all([
      this.query(businessQuery, params),
      this.query(countQuery, countParams),
    ]);

    return {
      businesses: businessResult.rows,
      totalCount: parseInt(countResult.rows[0].count),
    };
  }

  /**
   * PostGIS-optimized business search with sub-1-second performance
   */
  private async searchBusinessesPostGIS(searchQuery: BusinessSearchQuery): Promise<{
    businesses: Business[];
    totalCount: number;
  }> {
    const startTime = process.hrtime.bigint();
    const { lat, lng, radius = 25, category, search, page = 1, limit = 10 } = searchQuery;
    const offset = (page - 1) * limit;

    try {
      // Use the optimized PostGIS search function
      const searchSql = `
        SELECT * FROM search_businesses_by_location(
          $1::FLOAT, $2::FLOAT, $3::FLOAT, $4::TEXT[], $5::TEXT, $6::INTEGER, $7::INTEGER
        )
      `;

      const countSql = `
        SELECT count_businesses_by_location(
          $1::FLOAT, $2::FLOAT, $3::FLOAT, $4::TEXT[], $5::TEXT
        ) as total_count
      `;

      const categoryArray = category ? [category] : null;
      const searchParams = [
        lat,
        lng,
        radius,
        categoryArray,
        search || null,
        limit,
        offset,
      ];

      const countParams = searchParams.slice(0, 5); // Remove limit and offset

      // Execute queries in parallel for maximum performance
      const [businessResult, countResult] = await Promise.all([
        this.query(searchSql, searchParams),
        this.query(countSql, countParams),
      ]);

      const endTime = process.hrtime.bigint();
      const executionTimeMs = Number(endTime - startTime) / 1000000;

      // Log performance metrics for monitoring
      this.logQueryPerformance('searchBusinessesPostGIS', executionTimeMs, {
        lat, lng, radius, category, search, resultsCount: businessResult.rows.length
      });

      // Alert if performance degrades
      if (executionTimeMs > 200) {
        console.warn('PostGIS query performance warning:', {
          executionTimeMs,
          query: searchQuery,
          resultCount: businessResult.rows.length
        });
      }

      return {
        businesses: businessResult.rows.map(row => ({
          ...row,
          // Add distance to the business object for client use
          distance: parseFloat(row.distance_km),
        })),
        totalCount: parseInt(countResult.rows[0].total_count),
      };
    } catch (error) {
      const endTime = process.hrtime.bigint();
      const executionTimeMs = Number(endTime - startTime) / 1000000;

      console.error('PostGIS query error:', {
        error: error instanceof Error ? error.message : 'Unknown error',
        executionTimeMs,
        query: searchQuery
      });

      throw error;
    }
  }

  /**
   * Get businesses within a specific geometric area (polygon)
   */
  async getBusinessesInArea(polygon: { lat: number; lng: number }[]): Promise<Business[]> {
    const startTime = process.hrtime.bigint();

    try {
      // Convert polygon coordinates to PostGIS format
      const polygonString = polygon
        .map(point => `${point.lng} ${point.lat}`)
        .join(', ');

      const query = `
        SELECT *, 
               ST_X(location_point) as longitude,
               ST_Y(location_point) as latitude
        FROM businesses
        WHERE is_active = true
          AND location_point IS NOT NULL
          AND ST_Contains(
            ST_GeomFromText('POLYGON((${polygonString}, ${polygon[0].lng} ${polygon[0].lat}))', 4326),
            location_point
          )
        ORDER BY created_at DESC
      `;

      const result = await this.query(query, []);

      const endTime = process.hrtime.bigint();
      const executionTimeMs = Number(endTime - startTime) / 1000000;

      this.logQueryPerformance('getBusinessesInArea', executionTimeMs, {
        polygonPoints: polygon.length,
        resultsCount: result.rows.length
      });

      return result.rows;
    } catch (error) {
      console.error('Get businesses in area error:', error);
      throw error;
    }
  }

  /**
   * Get business density analysis for an area
   */
  async getBusinessDensityAnalysis(
    centerLat: number, 
    centerLng: number, 
    radiusKm: number
  ): Promise<{
    totalBusinesses: number;
    densityPerKm2: number;
    categoryBreakdown: { category: string; count: number }[];
    averageDistance: number;
  }> {
    const startTime = process.hrtime.bigint();

    try {
      const query = `
        WITH area_businesses AS (
          SELECT 
            categories,
            ST_Distance(
              ST_SetSRID(ST_MakePoint($2, $1), 4326)::geography,
              location_point::geography
            ) / 1000.0 AS distance_km
          FROM businesses
          WHERE is_active = true
            AND location_point IS NOT NULL
            AND ST_DWithin(
              ST_SetSRID(ST_MakePoint($2, $1), 4326)::geography,
              location_point::geography,
              $3 * 1000
            )
        ),
        category_counts AS (
          SELECT unnest(categories) as category, COUNT(*) as count
          FROM area_businesses
          GROUP BY category
          ORDER BY count DESC
        )
        SELECT 
          (SELECT COUNT(*) FROM area_businesses) as total_businesses,
          (SELECT ROUND(AVG(distance_km)::numeric, 2) FROM area_businesses) as avg_distance,
          (SELECT json_agg(json_build_object('category', category, 'count', count)) 
           FROM category_counts) as category_breakdown
      `;

      const result = await this.query(query, [centerLat, centerLng, radiusKm]);
      const row = result.rows[0];

      const totalBusinesses = parseInt(row.total_businesses) || 0;
      const areaKm2 = Math.PI * radiusKm * radiusKm;
      const densityPerKm2 = totalBusinesses / areaKm2;

      const endTime = process.hrtime.bigint();
      const executionTimeMs = Number(endTime - startTime) / 1000000;

      this.logQueryPerformance('getBusinessDensityAnalysis', executionTimeMs, {
        centerLat, centerLng, radiusKm, totalBusinesses
      });

      return {
        totalBusinesses,
        densityPerKm2: Math.round(densityPerKm2 * 100) / 100,
        categoryBreakdown: row.category_breakdown || [],
        averageDistance: parseFloat(row.avg_distance) || 0
      };
    } catch (error) {
      console.error('Business density analysis error:', error);
      throw error;
    }
  }

  /**
   * Find nearest businesses to a point
   */
  async findNearestBusinesses(
    lat: number, 
    lng: number, 
    limit: number = 5,
    category?: string
  ): Promise<(Business & { distance: number })[]> {
    const startTime = process.hrtime.bigint();

    try {
      let categoryFilter = '';
      const params: any[] = [lat, lng, limit];

      if (category) {
        categoryFilter = 'AND $4 = ANY(categories)';
        params.push(category);
      }

      const query = `
        SELECT *,
               ST_Distance(
                 ST_SetSRID(ST_MakePoint($2, $1), 4326)::geography,
                 location_point::geography
               ) / 1000.0 AS distance_km
        FROM businesses
        WHERE is_active = true
          AND location_point IS NOT NULL
          ${categoryFilter}
        ORDER BY location_point <-> ST_SetSRID(ST_MakePoint($2, $1), 4326)
        LIMIT $3
      `;

      const result = await this.query(query, params);

      const endTime = process.hrtime.bigint();
      const executionTimeMs = Number(endTime - startTime) / 1000000;

      this.logQueryPerformance('findNearestBusinesses', executionTimeMs, {
        lat, lng, limit, category, resultsCount: result.rows.length
      });

      return result.rows.map(row => ({
        ...row,
        distance: parseFloat(row.distance_km)
      }));
    } catch (error) {
      console.error('Find nearest businesses error:', error);
      throw error;
    }
  }

  /**
   * Log query performance for monitoring
   */
  private logQueryPerformance(queryType: string, executionTimeMs: number, metadata: any): void {
    const logEntry = {
      timestamp: new Date().toISOString(),
      queryType,
      executionTimeMs: Math.round(executionTimeMs * 100) / 100,
      metadata,
      performanceLevel: executionTimeMs < 50 ? 'excellent' : 
                       executionTimeMs < 200 ? 'good' : 
                       executionTimeMs < 500 ? 'acceptable' : 'poor'
    };

    // In production, this would go to a proper logging service
    console.log('Query Performance:', JSON.stringify(logEntry));

    // Could also send to metrics service like DataDog, New Relic, etc.
    // metricsService.recordQueryPerformance(logEntry);
  }

  /**
   * Update business information
   */
  async updateBusiness(businessId: string, updates: Partial<Business>): Promise<Business | null> {
    const updateFields: string[] = [];
    const values: any[] = [businessId];
    let paramIndex = 2;

    // Build dynamic update query
    Object.entries(updates).forEach(([key, value]) => {
      if (key !== 'id' && key !== 'owner_id' && key !== 'created_at' && key !== 'updated_at') {
        if (typeof value === 'object' && value !== null) {
          updateFields.push(`${key} = $${paramIndex}`);
          values.push(JSON.stringify(value));
        } else {
          updateFields.push(`${key} = $${paramIndex}`);
          values.push(value);
        }
        paramIndex++;
      }
    });

    if (updateFields.length === 0) {
      return this.findById(businessId);
    }

    updateFields.push('updated_at = CURRENT_TIMESTAMP');

    const query = `
      UPDATE businesses 
      SET ${updateFields.join(', ')}
      WHERE id = $1
      RETURNING *
    `;

    const result = await this.query(query, values);
    return result.rows[0] || null;
  }

  /**
   * Get businesses by category
   */
  async findByCategory(category: string, limit: number = 10): Promise<Business[]> {
    const query = `
      SELECT * FROM businesses 
      WHERE $1 = ANY(categories) AND is_active = true
      ORDER BY created_at DESC
      LIMIT $2
    `;
    const result = await this.query(query, [category, limit]);
    return result.rows;
  }

  /**
   * Get all unique categories
   */
  async getCategories(): Promise<string[]> {
    const query = `
      SELECT DISTINCT unnest(categories) as category 
      FROM businesses 
      WHERE is_active = true
      ORDER BY category
    `;
    const result = await this.query(query);
    return result.rows.map(row => row.category);
  }

  /**
   * Get business statistics for owner
   */
  async getBusinessStats(businessId: string): Promise<{
    totalViews: number;
    totalBookings: number;
    averageRating: number;
    totalReviews: number;
  }> {
    const query = `
      SELECT 
        COALESCE(AVG(r.rating), 0) as average_rating,
        COUNT(r.id) as total_reviews,
        COUNT(DISTINCT b.id) as total_bookings
      FROM businesses bus
      LEFT JOIN reviews r ON bus.id = r.business_id
      LEFT JOIN bookings b ON bus.id = b.business_id
      WHERE bus.id = $1
      GROUP BY bus.id
    `;

    const result = await this.query(query, [businessId]);
    const stats = result.rows[0] || {
      average_rating: 0,
      total_reviews: 0,
      total_bookings: 0,
    };

    return {
      totalViews: 0, // This would need to be tracked separately
      totalBookings: parseInt(stats.total_bookings),
      averageRating: parseFloat(stats.average_rating),
      totalReviews: parseInt(stats.total_reviews),
    };
  }

  /**
   * Check if user owns business
   */
  async isBusinessOwner(businessId: string, userId: string): Promise<boolean> {
    const query = 'SELECT 1 FROM businesses WHERE id = $1 AND owner_id = $2';
    const result = await this.query(query, [businessId, userId]);
    return result.rows.length > 0;
  }

  /**
   * Deactivate business (soft delete)
   */
  async deactivateBusiness(businessId: string): Promise<boolean> {
    const query = `
      UPDATE businesses 
      SET is_active = false, updated_at = CURRENT_TIMESTAMP
      WHERE id = $1
    `;
    const result = await this.query(query, [businessId]);
    return result.rowCount > 0;
  }
}

import { BaseRepository } from './BaseRepository.js';
import { CreateUserRequest, User, UserProfile } from '../types/User.js';
import bcrypt from 'bcryptjs';

export class UserRepository extends BaseRepository<User> {
  constructor() {
    super('users');
  }

  /**
   * Find user by email
   */
  async findByEmail(email: string): Promise<User | null> {
    const query = 'SELECT * FROM users WHERE email = $1';
    const result = await this.query(query, [email]);
    return result.rows[0] || null;
  }

  /**
   * Create a new user with hashed password
   */
  async createUser(userData: CreateUserRequest): Promise<User> {
    // Hash the password
    const saltRounds = 10;
    const password_hash = await bcrypt.hash(userData.password, saltRounds);

    // Prepare profile object
    const profile: UserProfile = {
      firstName: userData.firstName,
      lastName: userData.lastName,
      phone: userData.phone,
      locationPreferences: userData.locationPreferences,
    };

    const query = `
      INSERT INTO users (email, password_hash, role, profile, is_email_verified)
      VALUES ($1, $2, $3, $4, $5)
      RETURNING *
    `;

    const values = [
      userData.email,
      password_hash,
      userData.role,
      JSON.stringify(profile),
      false, // Default to unverified
    ];

    const result = await this.query(query, values);
    return result.rows[0];
  }

  /**
   * Update user profile
   */
  async updateProfile(userId: string, profileUpdates: Partial<UserProfile>): Promise<User | null> {
    // First get the current profile
    const currentUser = await this.findById(userId);
    if (!currentUser) {
      return null;
    }

    // Merge the updates with existing profile
    const updatedProfile = {
      ...currentUser.profile,
      ...profileUpdates,
    };

    const query = `
      UPDATE users 
      SET profile = $2, updated_at = CURRENT_TIMESTAMP
      WHERE id = $1
      RETURNING *
    `;

    const result = await this.query(query, [userId, JSON.stringify(updatedProfile)]);
    return result.rows[0] || null;
  }

  /**
   * Update last login timestamp
   */
  async updateLastLogin(userId: string): Promise<void> {
    const query = `
      UPDATE users 
      SET last_login_at = CURRENT_TIMESTAMP
      WHERE id = $1
    `;
    await this.query(query, [userId]);
  }

  /**
   * Verify user password
   */
  async verifyPassword(email: string, password: string): Promise<User | null> {
    const user = await this.findByEmail(email);
    if (!user) {
      return null;
    }

    const isValidPassword = await bcrypt.compare(password, user.password_hash);
    return isValidPassword ? user : null;
  }

  /**
   * Update email verification status
   */
  async verifyEmail(userId: string): Promise<User | null> {
    const query = `
      UPDATE users 
      SET is_email_verified = true, updated_at = CURRENT_TIMESTAMP
      WHERE id = $1
      RETURNING *
    `;

    const result = await this.query(query, [userId]);
    return result.rows[0] || null;
  }

  /**
   * Find users by role
   */
  async findByRole(role: string): Promise<User[]> {
    const query = 'SELECT * FROM users WHERE role = $1 ORDER BY created_at DESC';
    const result = await this.query(query, [role]);
    return result.rows;
  }

  /**
   * Update user password
   */
  async updatePassword(userId: string, newPassword: string): Promise<boolean> {
    const saltRounds = 10;
    const password_hash = await bcrypt.hash(newPassword, saltRounds);

    const query = `
      UPDATE users 
      SET password_hash = $2, updated_at = CURRENT_TIMESTAMP
      WHERE id = $1
    `;

    const result = await this.query(query, [userId, password_hash]);
    return result.rowCount > 0;
  }

  /**
   * Get users with pagination and filtering
   */
  async findUsersWithPagination(
    page: number = 1,
    limit: number = 10,
    role?: string
  ): Promise<{ users: User[]; totalCount: number }> {
    const offset = (page - 1) * limit;

    let whereClause = '';
    let countWhereClause = '';
    const params = [limit, offset];
    const countParams: any[] = [];

    if (role) {
      whereClause = 'WHERE role = $3';
      countWhereClause = 'WHERE role = $1';
      params.push(role);
      countParams.push(role);
    }

    const query = `
      SELECT * FROM users 
      ${whereClause}
      ORDER BY created_at DESC 
      LIMIT $1 OFFSET $2
    `;

    const countQuery = `SELECT COUNT(*) FROM users ${countWhereClause}`;

    const [usersResult, countResult] = await Promise.all([
      this.query(query, params),
      this.query(countQuery, countParams),
    ]);

    return {
      users: usersResult.rows,
      totalCount: parseInt(countResult.rows[0].count),
    };
  }
}

import { Router } from 'express';
import { BusinessService } from '../services/businessService.js';
import { validateBody, validateQuery } from '../middleware/validation.js';
import { authMiddleware, requireBusinessOwner, requireRole } from '../middleware/auth.js';
import { performanceMonitoring } from '../middleware/performanceMonitoring.js';
import { locationSecurityMiddleware } from '../middleware/locationSecurity.js';
import {
  businessMediaUploadSchema,
  businessSearchSchema,
  createBusinessSchema,
  updateBusinessSchema,
} from '../schemas/businessSchemas.js';
import { errorResponse, paginatedResponse, successResponse } from '../utils/responseUtils.js';
import {
  getCategoriesInLocation,
  getPopularAreas,
  handler as locationSearchHandler,
} from '../functions/business/locationSearch.js';
import { NextFunction, Request, Response } from 'express';

const router = Router();
const businessService = new BusinessService();

// Apply performance monitoring to all routes
router.use(performanceMonitoring);

/**
 * POST /api/businesses
 * Create a new business (business owners and admins only)
 */
router.post(
  '/',
  authMiddleware,
  requireRole(['business_owner', 'admin']),
  validateBody(createBusinessSchema),
  async (req: Request, res: Response, next: NextFunction) => {
    try {
      const ownerId = req.user?.id;
      if (!ownerId) {
        return errorResponse(res, 401, 'User not authenticated');
      }

      const business = await businessService.createBusiness(ownerId, req.body);
      return successResponse(res, 201, business, 'Business created successfully');
    } catch (error) {
      next(error);
    }
  }
);

/**
 * GET /api/businesses/search/location
 * Location-based business search with sub-1-second performance
 */
router.get(
  '/search/location',
  locationSecurityMiddleware,
  async (req: Request, res: Response, next: NextFunction) => {
    try {
      // Convert Express request to Lambda-compatible event for handler
      const event = {
        queryStringParameters: req.query as { [key: string]: string },
        headers: req.headers,
        requestContext: { requestId: `express-${Date.now()}` },
      } as any;

      const result = await locationSearchHandler(event, {} as any, {} as any);

      if (result.statusCode === 200) {
        const body = JSON.parse(result.body);
        res.set(result.headers || {});
        return res.status(200).json(body);
      } else {
        const errorBody = JSON.parse(result.body);
        return res.status(result.statusCode).json(errorBody);
      }
    } catch (error) {
      next(error);
    }
  }
);

/**
 * GET /api/businesses/search/location/categories
 * Get categories available in a specific location
 */
router.get(
  '/search/location/categories',
  locationSecurityMiddleware,
  async (req: Request, res: Response, next: NextFunction) => {
