import { BusinessRepository } from '../repositories/businessRepository.js';
import { GeocodingService } from './geocodingService.js';
import { ValidationService } from './validationService.js';
import { 
  Business, 
  BusinessLocation,
  BusinessResponseDto, 
  BusinessSearchQuery, 
  CreateBusinessRequest, 
  UpdateBusinessRequest 
} from '../types/Business.js';
import { createError } from '../middleware/errorHandler.js';

export class BusinessService {
  private businessRepository: BusinessRepository;
  private geocodingService: GeocodingService;

  constructor() {
    this.businessRepository = new BusinessRepository();
    this.geocodingService = new GeocodingService();
  }

  /**
   * Create a new business
   */
  async createBusiness(ownerId: string, businessData: CreateBusinessRequest): Promise<BusinessResponseDto> {
    // Validate required fields
    if (!businessData.name || !businessData.location || !businessData.categories?.length) {
      throw createError('Name, location, and at least one category are required', 400);
    }

    // Validate business name content
    const nameValidation = ValidationService.validateBusinessName(businessData.name);
    if (!nameValidation.isValid) {
      throw createError(`Invalid business name: ${nameValidation.errors.join(', ')}`, 400);
    }

    // Validate business hours
    if (businessData.hours) {
      const hoursValidation = ValidationService.validateBusinessHours(businessData.hours);
      if (!hoursValidation.isValid) {
        throw createError(`Invalid business hours: ${hoursValidation.errors.join(', ')}`, 400);
      }
    }

    // Normalize address and contact data
    const normalizedLocation = ValidationService.normalizeAddress(businessData.location);
    const normalizedContact = ValidationService.normalizeContact(businessData.contact);

    // Check business name uniqueness within geographic area (5-mile radius)
    await this.validateBusinessNameUniqueness(businessData.name, normalizedLocation, null);

    // Geocode the address if coordinates are not provided
    let locationData = normalizedLocation;
    if (!normalizedLocation.coordinates) {
      try {
        const geocoded = await this.geocodingService.geocodeAddress(
          normalizedLocation.address,
          normalizedLocation.city,
          normalizedLocation.state,
          normalizedLocation.zipCode
        );

        locationData = {
          ...normalizedLocation,
          coordinates: geocoded.coordinates,
          // Update with validated/formatted address data from geocoding
          address: geocoded.address,
          city: geocoded.city,
          state: geocoded.state,
          zipCode: geocoded.zipCode,
          country: geocoded.country,
        };
      } catch (error) {
        throw createError(`Address validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`, 400);
      }
    } else {
      // Validate provided coordinates
      const { lat, lng } = normalizedLocation.coordinates;
      if (!this.geocodingService.validateCoordinates(lat, lng)) {
        throw createError('Invalid location coordinates', 400);
      }
    }

    // Create business with validated and normalized data
    const businessToCreate = {
      ...businessData,
      location: locationData,
      contact: normalizedContact,
    };

    const business = await this.businessRepository.createBusiness(ownerId, businessToCreate);
    return this.mapToResponseDto(business);
  }

  /**
   * Get business by ID
   */
  async getBusinessById(businessId: string): Promise<BusinessResponseDto> {
    const business = await this.businessRepository.findById(businessId);
    if (!business) {
      throw createError('Business not found', 404);
    }
    return this.mapToResponseDto(business);
  }

  /**
   * Update business
   */
  async updateBusiness(
    businessId: string, 
    ownerId: string, 
    updates: UpdateBusinessRequest
  ): Promise<BusinessResponseDto> {
    // Check if user owns the business
    const isOwner = await this.businessRepository.isBusinessOwner(businessId, ownerId);
    if (!isOwner) {
      throw createError('Business not found or access denied', 404);
    }

    // Get current business data for validation
    const currentBusiness = await this.businessRepository.findById(businessId);
    if (!currentBusiness) {
      throw createError('Business not found', 404);
    }

    // Check business name uniqueness if name is being updated
    if (updates.name && updates.name !== currentBusiness.name) {
      const locationForCheck = updates.location || currentBusiness.location;
      await this.validateBusinessNameUniqueness(updates.name, locationForCheck, businessId);
    }

    // Validate coordinates if provided
    if (updates.location?.coordinates) {
      const { lat, lng } = updates.location.coordinates;
      if (!this.geocodingService.validateCoordinates(lat, lng)) {
        throw createError('Invalid location coordinates', 400);
      }
    }

    const updatedBusiness = await this.businessRepository.updateBusiness(businessId, updates);
    if (!updatedBusiness) {
      throw createError('Failed to update business', 500);
    }

    return this.mapToResponseDto(updatedBusiness);
  }

  /**
   * Search businesses with location and filters
   */
  async searchBusinesses(searchQuery: BusinessSearchQuery): Promise<{
    businesses: BusinessResponseDto[];
    totalCount: number;
  }> {
    // Validate pagination parameters
    const page = Math.max(1, searchQuery.page || 1);
    const limit = Math.min(Math.max(1, searchQuery.limit || 10), 50);

    // Validate location coordinates if provided
    if (searchQuery.lat !== undefined || searchQuery.lng !== undefined) {
      if (searchQuery.lat === undefined || searchQuery.lng === undefined) {
        throw createError('Both latitude and longitude are required for location search', 400);
      }

      if (searchQuery.lat < -90 || searchQuery.lat > 90 || 
          searchQuery.lng < -180 || searchQuery.lng > 180) {
        throw createError('Invalid location coordinates', 400);
      }
    }

    // Validate radius
    if (searchQuery.radius && (searchQuery.radius < 1 || searchQuery.radius > 100)) {
      throw createError('Radius must be between 1 and 100 miles', 400);
    }

    const normalizedQuery = {
      ...searchQuery,
      page,
      limit,
    };

    const { businesses, totalCount } = await this.businessRepository.searchBusinesses(normalizedQuery);

    return {
      businesses: businesses.map(business => this.mapToResponseDto(business)),
      totalCount,
    };
  }

  /**
   * Get businesses owned by user
   */
  async getBusinessesByOwner(ownerId: string): Promise<BusinessResponseDto[]> {
    const businesses = await this.businessRepository.findByOwnerId(ownerId);
    return businesses.map(business => this.mapToResponseDto(business));
  }

  /**
   * Get businesses by category
   */
  async getBusinessesByCategory(category: string, limit: number = 10): Promise<BusinessResponseDto[]> {
    const businesses = await this.businessRepository.findByCategory(category, limit);
    return businesses.map(business => this.mapToResponseDto(business));
  }

  /**
   * Get all available categories
   */
  async getCategories(): Promise<string[]> {
    return await this.businessRepository.getCategories();
  }

  /**
   * Get business statistics for owner
   */
  async getBusinessStats(businessId: string, ownerId: string): Promise<{
    totalViews: number;
    totalBookings: number;
    averageRating: number;
    totalReviews: number;
  }> {
    // Check if user owns the business
    const isOwner = await this.businessRepository.isBusinessOwner(businessId, ownerId);
    if (!isOwner) {
      throw createError('Business not found or access denied', 404);
    }

    return await this.businessRepository.getBusinessStats(businessId);
  }

  /**
   * Delete business (deactivate)
   */
  async deleteBusiness(businessId: string, ownerId: string): Promise<void> {
    // Check if user owns the business
    const isOwner = await this.businessRepository.isBusinessOwner(businessId, ownerId);
    if (!isOwner) {
      throw createError('Business not found or access denied', 404);
    }

    const success = await this.businessRepository.deactivateBusiness(businessId);
    if (!success) {
      throw createError('Failed to delete business', 500);
    }
  }

  /**
   * Calculate distance between two coordinates
   */
  private calculateDistance(lat1: number, lng1: number, lat2: number, lng2: number): number {
    const R = 3959; // Earth's radius in miles
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = 
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  /**
   * Map Business entity to response DTO
   */
  private mapToResponseDto(business: Business): BusinessResponseDto {
    const mapped: BusinessResponseDto = {
      id: business.id,
      owner_id: business.owner_id,
      name: business.name,
      description: business.description,
      location: business.location,
      categories: business.categories,
      hours: business.hours,
      contact: business.contact,
      media: business.media,
      services: business.services,
      is_active: business.is_active,
      created_at: business.created_at,
      updated_at: business.updated_at,
    };

    // Add distance if it exists (from location-based searches)
    if ('distance' in business && typeof (business as any).distance === 'number') {
      mapped.distance = (business as any).distance;
    }

    return mapped;
  }

  /**
   * Validate business name uniqueness within geographic area
   */
  private async validateBusinessNameUniqueness(
    name: string, 
    location: BusinessLocation, 
    excludeBusinessId?: string | null
  ): Promise<void> {
    if (!location.coordinates) {
      // Skip uniqueness check if no coordinates (will be geocoded later)
      return;
    }

    const { lat, lng } = location.coordinates;
    const searchRadius = 5; // 5-mile radius for name uniqueness check

    const searchQuery = {
      lat,
      lng,
      radius: searchRadius,
      search: name,
      page: 1,
      limit: 10
    };

    const { businesses } = await this.businessRepository.searchBusinesses(searchQuery);

    // Check for exact name matches (case-insensitive)
    const duplicates = businesses.filter(business => {
      const isDifferentBusiness = excludeBusinessId ? business.id !== excludeBusinessId : true;
      const isSameName = business.name.toLowerCase().trim() === name.toLowerCase().trim();
      return isDifferentBusiness && isSameName;
    });

    if (duplicates.length > 0) {
      throw createError(
        `A business with the name "${name}" already exists within 5 miles of this location. Please choose a different name or add a distinguishing detail.`,
        409
      );
    }
  }

  /**
   * Health check for business service
   */
  async healthCheck(): Promise<boolean> {
    return await this.businessRepository.healthCheck();
  }
}

import {
  AdminCreateUserCommand,
  AdminGetUserCommand,
  AdminInitiateAuthCommand,
  AdminSetUserPasswordCommand,
  AdminUpdateUserAttributesCommand,
  AttributeType,
  CognitoIdentityProviderClient,
  ConfirmForgotPasswordCommand,
  ForgotPasswordCommand,
} from '@aws-sdk/client-cognito-identity-provider';
import crypto from 'crypto';
import { config } from '../config/environment';
import { CreateUserRequest, User } from '@buy-locals/shared';

const cognitoClient = new CognitoIdentityProviderClient({
  region: config.awsRegion,
});

export class CognitoService {
  /**
   * Register a new user in Cognito
   */
  async registerUser(
    userData: CreateUserRequest
  ): Promise<{ userId: string; tempPassword: string }> {
    const tempPassword = this.generateTempPassword();

    const userAttributes: AttributeType[] = [
      { Name: 'email', Value: userData.email },
      { Name: 'email_verified', Value: 'false' },
      { Name: 'custom:role', Value: userData.role || 'consumer' },
      { Name: 'given_name', Value: userData.firstName },
      { Name: 'family_name', Value: userData.lastName },
    ];

    if (userData.phone) {
      userAttributes.push({ Name: 'phone_number', Value: userData.phone });
    }

    try {
      // Create user in Cognito
      const createUserCommand = new AdminCreateUserCommand({
        UserPoolId: config.cognitoUserPoolId,
        Username: userData.email,
        UserAttributes: userAttributes,
        TemporaryPassword: tempPassword,
        MessageAction: 'SUPPRESS', // Don't send welcome email initially
      });

      const createResult = await cognitoClient.send(createUserCommand);

      // Set permanent password
      const setPasswordCommand = new AdminSetUserPasswordCommand({
        UserPoolId: config.cognitoUserPoolId,
        Username: userData.email,
        Password: userData.password,
        Permanent: true,
      });

      await cognitoClient.send(setPasswordCommand);

      return {
        userId: createResult.User?.Username || userData.email,
        tempPassword,
      };
    } catch (error) {
      console.error('Error registering user:', error);
      throw new Error('Failed to register user');
    }
  }

  /**
   * Authenticate user and get tokens
   */
  async loginUser(
    email: string,
    password: string
  ): Promise<{
    accessToken: string;
    refreshToken: string;
    idToken: string;
  }> {
    try {
      const authCommand = new AdminInitiateAuthCommand({
        UserPoolId: config.cognitoUserPoolId,
        ClientId: config.cognitoClientId,
        AuthFlow: 'ADMIN_NO_SRP_AUTH',
        AuthParameters: {
          USERNAME: email,
          PASSWORD: password,
          SECRET_HASH: this.calculateSecretHash(email),
        },
      });

      const authResult = await cognitoClient.send(authCommand);

      if (authResult.AuthenticationResult) {
        return {
          accessToken: authResult.AuthenticationResult.AccessToken || '',
          refreshToken: authResult.AuthenticationResult.RefreshToken || '',
          idToken: authResult.AuthenticationResult.IdToken || '',
        };
      } else {
        throw new Error('Authentication failed');
      }
    } catch (error) {
      console.error('Error logging in user:', error);
      throw new Error('Invalid credentials');
    }
  }

  /**
   * Refresh access token using refresh token
   */
  async refreshToken(refreshToken: string): Promise<{
    accessToken: string;
    idToken: string;
  }> {
    try {
      const refreshCommand = new AdminInitiateAuthCommand({
        UserPoolId: config.cognitoUserPoolId,
        ClientId: config.cognitoClientId,
        AuthFlow: 'REFRESH_TOKEN_AUTH',
        AuthParameters: {
          REFRESH_TOKEN: refreshToken,
        },
      });

      const refreshResult = await cognitoClient.send(refreshCommand);

      if (refreshResult.AuthenticationResult) {
        return {
          accessToken: refreshResult.AuthenticationResult.AccessToken || '',
          idToken: refreshResult.AuthenticationResult.IdToken || '',
        };
      } else {
        throw new Error('Token refresh failed');
      }
    } catch (error) {
      console.error('Error refreshing token:', error);
      throw new Error('Failed to refresh token');
    }
  }

  /**
   * Get user information from Cognito
   */
  async getUser(username: string): Promise<Partial<User>> {
    try {
      const getUserCommand = new AdminGetUserCommand({
        UserPoolId: config.cognitoUserPoolId,
        Username: username,
      });

      const result = await cognitoClient.send(getUserCommand);

      const attributes = result.UserAttributes || [];
      const getAttr = (name: string) => attributes.find(attr => attr.Name === name)?.Value || '';

      return {
        id: result.Username || username,
        email: getAttr('email'),
        role: getAttr('custom:role') as 'consumer' | 'business_owner' | 'admin',
        profile: {
          firstName: getAttr('given_name'),
          lastName: getAttr('family_name'),
          phone: getAttr('phone_number') || undefined,
        },
        isEmailVerified: getAttr('email_verified') === 'true',
        createdAt: result.UserCreateDate || new Date(),
        updatedAt: result.UserLastModifiedDate || new Date(),
      };
    } catch (error) {
      console.error('Error getting user:', error);
      throw new Error('User not found');
    }
  }

  /**
   * Update user profile attributes
   */
  async updateUserProfile(
    username: string,
    updates: {
      firstName?: string;
      lastName?: string;
      phone?: string;
    }
  ): Promise<void> {
    try {
      const attributes: AttributeType[] = [];

      if (updates.firstName) {
        attributes.push({ Name: 'given_name', Value: updates.firstName });
      }
      if (updates.lastName) {
        attributes.push({ Name: 'family_name', Value: updates.lastName });
      }
      if (updates.phone) {
        attributes.push({ Name: 'phone_number', Value: updates.phone });
      }

      if (attributes.length > 0) {
        const updateCommand = new AdminUpdateUserAttributesCommand({
          UserPoolId: config.cognitoUserPoolId,
          Username: username,
          UserAttributes: attributes,
        });

        await cognitoClient.send(updateCommand);
      }
    } catch (error) {
      console.error('Error updating user profile:', error);
      throw new Error('Failed to update profile');
    }
  }

  /**
   * Initiate password reset
   */
  async forgotPassword(email: string): Promise<void> {
    try {
      const forgotPasswordCommand = new ForgotPasswordCommand({
        ClientId: config.cognitoClientId,
        Username: email,
        SecretHash: this.calculateSecretHash(email),
      });

      await cognitoClient.send(forgotPasswordCommand);
    } catch (error) {
      console.error('Error initiating password reset:', error);
      throw new Error('Failed to initiate password reset');
    }
  }

  /**
   * Confirm password reset with code
   */
  async confirmForgotPassword(
    email: string,
    confirmationCode: string,
    newPassword: string
  ): Promise<void> {
    try {
      const confirmCommand = new ConfirmForgotPasswordCommand({
        ClientId: config.cognitoClientId,
        Username: email,
        ConfirmationCode: confirmationCode,
        Password: newPassword,
        SecretHash: this.calculateSecretHash(email),
      });

      await cognitoClient.send(confirmCommand);
    } catch (error) {
      console.error('Error confirming password reset:', error);
      throw new Error('Failed to reset password');
    }
  }

  /**
   * Calculate secret hash for Cognito operations (if client secret is configured)
   */
  private calculateSecretHash(username: string): string {
    if (!config.cognitoClientSecret) {
      return '';
    }

    return crypto
      .createHmac('sha256', config.cognitoClientSecret)
      .update(username + config.cognitoClientId)
      .digest('base64');
  }

  /**
   * Generate a temporary password for new users
   */
  private generateTempPassword(): string {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
    let password = '';
    for (let i = 0; i < 12; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return password;
  }
}

import { config } from '../config/environment.js';

export interface GeocodingResult {
  address: string;
  city: string;
  state: string;
  zipCode: string;
  country: string;
  coordinates: {
    lat: number;
    lng: number;
  };
  formattedAddress: string;
}

export class GeocodingService {
  private apiKey: string;
  private baseUrl = 'https://maps.googleapis.com/maps/api/geocode/json';
  private cache = new Map<string, { result: GeocodingResult; timestamp: number }>();
  private cacheTtl = 24 * 60 * 60 * 1000; // 24 hours

  constructor() {
    this.apiKey = config.googleMapsApiKey;
    if (!this.apiKey) {
      throw new Error('Google Maps API key is required for geocoding');
    }
  }

  /**
   * Validate and geocode an address using Google Maps Geocoding API
   */
  async geocodeAddress(address: string, city: string, state: string, zipCode: string): Promise<GeocodingResult> {
    const fullAddress = `${address}, ${city}, ${state} ${zipCode}`;

    // Check cache first
    const cacheKey = `geocode:${fullAddress.toLowerCase()}`;
    const cached = this.cache.get(cacheKey);
    if (cached && Date.now() - cached.timestamp < this.cacheTtl) {
      return cached.result;
    }

    try {
      const url = new URL(this.baseUrl);
      url.searchParams.append('address', fullAddress);
      url.searchParams.append('key', this.apiKey);

      const response = await fetch(url.toString());

      if (!response.ok) {
        throw new Error(`Geocoding API request failed: ${response.status}`);
      }

      const data = await response.json();

      if (data.status !== 'OK' || !data.results?.length) {
        throw new Error(`Address validation failed: ${data.status || 'No results found'}`);
      }

      const result = data.results[0];
      const location = result.geometry.location;

      // Extract address components
      const addressComponents = result.address_components;
      const extractedAddress = this.extractAddressComponents(addressComponents);

      const geocodingResult = {
        address: extractedAddress.address || address,
        city: extractedAddress.city || city,
        state: extractedAddress.state || state,
        zipCode: extractedAddress.zipCode || zipCode,
        country: extractedAddress.country || 'US',
        coordinates: {
          lat: location.lat,
          lng: location.lng,
        },
        formattedAddress: result.formatted_address,
      };

      // Cache the successful result
      this.cache.set(cacheKey, {
        result: geocodingResult,
        timestamp: Date.now()
      });

      return geocodingResult;
    } catch (error) {
      console.error('Geocoding API error:', {
        address: fullAddress,
        error: error instanceof Error ? error.message : 'Unknown error'
      });
      throw new Error(`Geocoding failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Reverse geocode coordinates to get address information
   */
  async reverseGeocode(lat: number, lng: number): Promise<GeocodingResult> {
    try {
      const url = new URL(this.baseUrl);
      url.searchParams.append('latlng', `${lat},${lng}`);
      url.searchParams.append('key', this.apiKey);

      const response = await fetch(url.toString());

      if (!response.ok) {
        throw new Error(`Reverse geocoding API request failed: ${response.status}`);
      }

      const data = await response.json();

      if (data.status !== 'OK' || !data.results?.length) {
        throw new Error(`Reverse geocoding failed: ${data.status || 'No results found'}`);
      }

      const result = data.results[0];
      const addressComponents = result.address_components;
      const extractedAddress = this.extractAddressComponents(addressComponents);

      return {
        address: extractedAddress.address || '',
        city: extractedAddress.city || '',
        state: extractedAddress.state || '',
        zipCode: extractedAddress.zipCode || '',
        country: extractedAddress.country || 'US',
        coordinates: {
          lat,
          lng,
        },
        formattedAddress: result.formatted_address,
      };
    } catch (error) {
      console.error('Reverse geocoding API error:', {
        coordinates: { lat, lng },
        error: error instanceof Error ? error.message : 'Unknown error'
      });
      throw new Error(`Reverse geocoding failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Validate coordinates are within reasonable bounds
   */
  validateCoordinates(lat: number, lng: number): boolean {
    return lat >= -90 && lat <= 90 && lng >= -180 && lng <= 180;
  }

  /**
   * Extract structured address information from Google Maps address components
   */
  private extractAddressComponents(components: any[]): {
    address?: string;
    city?: string;
    state?: string;
    zipCode?: string;
    country?: string;
  } {
    const extracted: any = {};

    for (const component of components) {
      const types = component.types;

      if (types.includes('street_number')) {
        extracted.streetNumber = component.long_name;
      } else if (types.includes('route')) {
        extracted.streetName = component.long_name;
      } else if (types.includes('locality')) {
        extracted.city = component.long_name;
      } else if (types.includes('administrative_area_level_1')) {
        extracted.state = component.short_name;
      } else if (types.includes('postal_code')) {
        extracted.zipCode = component.long_name;
      } else if (types.includes('country')) {
        extracted.country = component.short_name;
      }
    }

    // Combine street number and name for full address
    if (extracted.streetNumber && extracted.streetName) {
      extracted.address = `${extracted.streetNumber} ${extracted.streetName}`;
    } else if (extracted.streetName) {
      extracted.address = extracted.streetName;
    }

    return extracted;
  }
}

import { redisClient } from '../config/redis.js';

export interface LocationSearchMetrics {
  totalSearches: number;
  averageExecutionTime: number;
  cacheHitRate: number;
  errorRate: number;
  performanceDistribution: {
    excellent: number; // <50ms
    good: number; // 50-200ms
    acceptable: number; // 200-500ms
    poor: number; // >500ms
  };
  popularSearchAreas: Array<{
    lat: number;
    lng: number;
    count: number;
    averageRadius: number;
  }>;
}

export interface LocationAlert {
  id: string;
  type: 'performance' | 'error' | 'cache' | 'database';
  severity: 'low' | 'medium' | 'high' | 'critical';
  title: string;
  description: string;
  timestamp: string;
  metadata: any;
  resolved: boolean;
}

export interface PerformanceThresholds {
  maxExecutionTimeMs: number;
  minCacheHitRate: number;
  maxErrorRate: number;
  maxConcurrentSearches: number;
}

class LocationMonitoringService {
  private readonly METRICS_PREFIX = 'location_metrics';
  private readonly ALERTS_PREFIX = 'location_alerts';
  private readonly THRESHOLDS: PerformanceThresholds = {
    maxExecutionTimeMs: 1000,
    minCacheHitRate: 0.7,
    maxErrorRate: 0.05,
    maxConcurrentSearches: 100,
  };

  /**
   * Record a location search execution
   */
  async recordSearchExecution(
    executionTimeMs: number,
    cacheHit: boolean,
    error: boolean,
    searchParams: {
      lat: number;
      lng: number;
      radius?: number;
      category?: string[];
      search?: string;
    }
  ): Promise<void> {
    try {
      if (!redisClient.isReady) return;

      const timestamp = Date.now();
      const hour = new Date().getHours();
      const date = new Date().toISOString().split('T')[0];

      // Update metrics
      const promises = [
        // Total searches counter
        redisClient.incr(`${this.METRICS_PREFIX}:total_searches:${date}`),
        redisClient.incr(`${this.METRICS_PREFIX}:total_searches:${date}:${hour}`),

        // Execution time tracking
        redisClient.lpush(
          `${this.METRICS_PREFIX}:execution_times:${date}`,
          executionTimeMs.toString()
        ),

        // Cache hit tracking
        redisClient.incr(
          `${this.METRICS_PREFIX}:${cacheHit ? 'cache_hits' : 'cache_misses'}:${date}`
        ),

        // Error tracking
        error && redisClient.incr(`${this.METRICS_PREFIX}:errors:${date}`),

        // Popular search locations
        this.recordPopularLocation(searchParams.lat, searchParams.lng, searchParams.radius || 25),

        // Performance distribution
        this.recordPerformanceBucket(executionTimeMs, date),
      ];

      await Promise.all(promises.filter(Boolean));

      // Set TTL on daily metrics (keep for 30 days)
      await Promise.all([
        redisClient.expire(`${this.METRICS_PREFIX}:total_searches:${date}`, 30 * 24 * 3600),
        redisClient.expire(`${this.METRICS_PREFIX}:execution_times:${date}`, 30 * 24 * 3600),
        redisClient.expire(`${this.METRICS_PREFIX}:cache_hits:${date}`, 30 * 24 * 3600),
        redisClient.expire(`${this.METRICS_PREFIX}:cache_misses:${date}`, 30 * 24 * 3600),
        redisClient.expire(`${this.METRICS_PREFIX}:errors:${date}`, 30 * 24 * 3600),
      ]);

      // Check if we need to trigger alerts
      await this.checkAndTriggerAlerts({
        executionTimeMs,
        cacheHit,
        error,
        timestamp,
      });
    } catch (monitoringError) {
      console.error('Failed to record search metrics:', monitoringError);
      // Don't let monitoring errors affect the main functionality
    }
  }

  /**
   * Record performance bucket distribution
   */
  private async recordPerformanceBucket(executionTimeMs: number, date: string): Promise<void> {
    let bucket: string;

    if (executionTimeMs < 50) {
      bucket = 'excellent';
    } else if (executionTimeMs < 200) {
      bucket = 'good';
    } else if (executionTimeMs < 500) {
      bucket = 'acceptable';
    } else {
      bucket = 'poor';
    }

    await redisClient.incr(`${this.METRICS_PREFIX}:perf_${bucket}:${date}`);
    await redisClient.expire(`${this.METRICS_PREFIX}:perf_${bucket}:${date}`, 30 * 24 * 3600);
  }

  /**
   * Record popular search locations
   */
  private async recordPopularLocation(lat: number, lng: number, radius: number): Promise<void> {
    try {
      // Round coordinates to create location clusters
      const roundedLat = Math.round(lat * 100) / 100; // ~1km precision
      const roundedLng = Math.round(lng * 100) / 100;
      const locationKey = `${roundedLat},${roundedLng}`;

      const date = new Date().toISOString().split('T')[0];

      await Promise.all([
        redisClient.zincrby(`${this.METRICS_PREFIX}:popular_locations:${date}`, 1, locationKey),
        redisClient.hset(
          `${this.METRICS_PREFIX}:location_radii:${date}`,
          locationKey,
          radius.toString()
        ),
        redisClient.expire(`${this.METRICS_PREFIX}:popular_locations:${date}`, 30 * 24 * 3600),
        redisClient.expire(`${this.METRICS_PREFIX}:location_radii:${date}`, 30 * 24 * 3600),
      ]);
    } catch (error) {
      console.error('Failed to record popular location:', error);
    }
  }

  /**
   * Get current location search metrics
   */
  async getLocationSearchMetrics(days: number = 7): Promise<LocationSearchMetrics> {
    try {
      if (!redisClient.isReady) {
        return this.getEmptyMetrics();
      }

      const dates = Array.from({ length: days }, (_, i) => {
        const date = new Date();
        date.setDate(date.getDate() - i);
        return date.toISOString().split('T')[0];
      });

      const metrics = await this.aggregateMetrics(dates);
      return metrics;
    } catch (error) {
      console.error('Failed to get location metrics:', error);
      return this.getEmptyMetrics();
    }
  }

  /**
   * Aggregate metrics across multiple dates
   */
  private async aggregateMetrics(dates: string[]): Promise<LocationSearchMetrics> {
    const promises = dates.map(date => this.getMetricsForDate(date));
    const dailyMetrics = await Promise.all(promises);

    // Aggregate all metrics
    let totalSearches = 0;
    let totalExecutionTime = 0;
    let totalExecutionSamples = 0;
    let totalCacheHits = 0;
    let totalCacheMisses = 0;
    let totalErrors = 0;
    const performanceDistribution = {
      excellent: 0,
      good: 0,
      acceptable: 0,
      poor: 0,
    };
    const locationCounts = new Map<string, number>();

    for (const dayMetrics of dailyMetrics) {
      totalSearches += dayMetrics.totalSearches;
      totalExecutionTime += dayMetrics.totalExecutionTime;
      totalExecutionSamples += dayMetrics.executionSamples;
      totalCacheHits += dayMetrics.cacheHits;
      totalCacheMisses += dayMetrics.cacheMisses;
      totalErrors += dayMetrics.errors;

      performanceDistribution.excellent += dayMetrics.performanceDistribution.excellent;
      performanceDistribution.good += dayMetrics.performanceDistribution.good;
      performanceDistribution.acceptable += dayMetrics.performanceDistribution.acceptable;
      performanceDistribution.poor += dayMetrics.performanceDistribution.poor;

      // Merge location counts
      for (const [location, count] of dayMetrics.popularLocations) {
        locationCounts.set(location, (locationCounts.get(location) || 0) + count);
      }
    }

    // Calculate derived metrics
    const averageExecutionTime =
      totalExecutionSamples > 0 ? totalExecutionTime / totalExecutionSamples : 0;

    const cacheHitRate =
      totalCacheHits + totalCacheMisses > 0
        ? totalCacheHits / (totalCacheHits + totalCacheMisses)
        : 0;

    const errorRate = totalSearches > 0 ? totalErrors / totalSearches : 0;

    // Top popular search areas
    const popularSearchAreas = Array.from(locationCounts.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 10)
      .map(([location, count]) => {
        const [lat, lng] = location.split(',').map(Number);
        return {
          lat,
          lng,
          count,
          averageRadius: 25, // Default, could be calculated from stored radii
        };
      });

    return {
      totalSearches,
      averageExecutionTime,
      cacheHitRate,
      errorRate,
      performanceDistribution,
      popularSearchAreas,
    };
  }

  /**
   * Get metrics for a specific date
   */
  private async getMetricsForDate(date: string): Promise<{
    totalSearches: number;
    totalExecutionTime: number;
    executionSamples: number;
    cacheHits: number;
    cacheMisses: number;
    errors: number;
    performanceDistribution: {
      excellent: number;
      good: number;
      acceptable: number;
      poor: number;
    };
    popularLocations: Map<string, number>;
  }> {
    try {
      const [
        totalSearches,
        executionTimes,
        cacheHits,
        cacheMisses,
        errors,
        perfExcellent,
        perfGood,
        perfAcceptable,
        perfPoor,
        popularLocations,
      ] = await Promise.all([
        redisClient
          .get(`${this.METRICS_PREFIX}:total_searches:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient.lrange(`${this.METRICS_PREFIX}:execution_times:${date}`, 0, -1),
        redisClient
          .get(`${this.METRICS_PREFIX}:cache_hits:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient
          .get(`${this.METRICS_PREFIX}:cache_misses:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient.get(`${this.METRICS_PREFIX}:errors:${date}`).then(val => parseInt(val || '0')),
        redisClient
          .get(`${this.METRICS_PREFIX}:perf_excellent:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient
          .get(`${this.METRICS_PREFIX}:perf_good:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient
          .get(`${this.METRICS_PREFIX}:perf_acceptable:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient
          .get(`${this.METRICS_PREFIX}:perf_poor:${date}`)
          .then(val => parseInt(val || '0')),
        redisClient.zrevrange(
          `${this.METRICS_PREFIX}:popular_locations:${date}`,
          0,
          9,
          'WITHSCORES'
        ),
      ]);

      const totalExecutionTime = executionTimes.reduce((sum, time) => sum + parseInt(time), 0);
      const popularLocationsMap = new Map<string, number>();

      for (let i = 0; i < popularLocations.length; i += 2) {
        const location = popularLocations[i];
        const count = parseInt(popularLocations[i + 1]);
        popularLocationsMap.set(location, count);
      }

      return {
        totalSearches,
        totalExecutionTime,
        executionSamples: executionTimes.length,
        cacheHits,
        cacheMisses,
        errors,
        performanceDistribution: {
          excellent: perfExcellent,
          good: perfGood,
          acceptable: perfAcceptable,
          poor: perfPoor,
        },
        popularLocations: popularLocationsMap,
      };
    } catch (error) {
      console.error(`Failed to get metrics for date ${date}:`, error);
      return {
        totalSearches: 0,
        totalExecutionTime: 0,
        executionSamples: 0,
        cacheHits: 0,
        cacheMisses: 0,
        errors: 0,
        performanceDistribution: { excellent: 0, good: 0, acceptable: 0, poor: 0 },
        popularLocations: new Map(),
      };
    }
  }

  /**
   * Check performance thresholds and trigger alerts
   */
  private async checkAndTriggerAlerts(params: {
    executionTimeMs: number;
    cacheHit: boolean;
    error: boolean;
    timestamp: number;
  }): Promise<void> {
    const { executionTimeMs, error, timestamp } = params;

    // Performance alert
    if (executionTimeMs > this.THRESHOLDS.maxExecutionTimeMs) {
      await this.createAlert({
        type: 'performance',
        severity: executionTimeMs > 2000 ? 'high' : 'medium',
        title: 'Location Search Performance Degraded',
        description: `Search execution time ${executionTimeMs}ms exceeds threshold ${this.THRESHOLDS.maxExecutionTimeMs}ms`,
        metadata: { executionTimeMs, threshold: this.THRESHOLDS.maxExecutionTimeMs },
      });
    }

    // Error alert
    if (error) {
      await this.createAlert({
        type: 'error',
        severity: 'medium',
        title: 'Location Search Error',
        description: 'Location search request failed',
        metadata: { timestamp },
      });
    }

    // Check aggregated metrics for system-wide issues
    await this.checkAggregatedAlerts();
  }

  /**
   * Check for system-wide performance issues
   */
  private async checkAggregatedAlerts(): Promise<void> {
    try {
      const recentMetrics = await this.getLocationSearchMetrics(1); // Last day

      // Cache hit rate alert
      if (
        recentMetrics.cacheHitRate < this.THRESHOLDS.minCacheHitRate &&
        recentMetrics.totalSearches > 10
      ) {
        await this.createAlert({
          type: 'cache',
          severity: 'medium',
          title: 'Low Cache Hit Rate',
          description: `Cache hit rate ${(recentMetrics.cacheHitRate * 100).toFixed(1)}% is below threshold ${this.THRESHOLDS.minCacheHitRate * 100}%`,
          metadata: {
            cacheHitRate: recentMetrics.cacheHitRate,
            threshold: this.THRESHOLDS.minCacheHitRate,
            totalSearches: recentMetrics.totalSearches,
          },
        });
      }

      // Error rate alert
      if (
        recentMetrics.errorRate > this.THRESHOLDS.maxErrorRate &&
        recentMetrics.totalSearches > 10
      ) {
        await this.createAlert({
          type: 'error',
          severity: recentMetrics.errorRate > 0.1 ? 'high' : 'medium',
          title: 'High Error Rate',
          description: `Error rate ${(recentMetrics.errorRate * 100).toFixed(1)}% is above threshold ${this.THRESHOLDS.maxErrorRate * 100}%`,
          metadata: {
            errorRate: recentMetrics.errorRate,
            threshold: this.THRESHOLDS.maxErrorRate,
            totalSearches: recentMetrics.totalSearches,
          },
        });
      }

      // Average performance alert
      if (
        recentMetrics.averageExecutionTime > this.THRESHOLDS.maxExecutionTimeMs &&
        recentMetrics.totalSearches > 10
      ) {
        await this.createAlert({
          type: 'performance',
          severity: 'medium',
          title: 'Average Performance Degraded',
          description: `Average execution time ${recentMetrics.averageExecutionTime.toFixed(0)}ms exceeds threshold`,
          metadata: {
            averageExecutionTime: recentMetrics.averageExecutionTime,
            threshold: this.THRESHOLDS.maxExecutionTimeMs,
            totalSearches: recentMetrics.totalSearches,
          },
        });
      }
    } catch (error) {
      console.error('Failed to check aggregated alerts:', error);
    }
  }

  /**
   * Create an alert
   */
  private async createAlert(
    alert: Omit<LocationAlert, 'id' | 'timestamp' | 'resolved'>
  ): Promise<void> {
    try {
      const alertWithId: LocationAlert = {
        ...alert,
        id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        timestamp: new Date().toISOString(),
        resolved: false,
      };

      // Store alert in Redis
      await redisClient.hset(
        `${this.ALERTS_PREFIX}:active`,
        alertWithId.id,
        JSON.stringify(alertWithId)
      );

      // Add to alerts timeline
      await redisClient.zadd(`${this.ALERTS_PREFIX}:timeline`, Date.now(), alertWithId.id);

      // Set TTL on timeline (keep for 90 days)
      await redisClient.expire(`${this.ALERTS_PREFIX}:timeline`, 90 * 24 * 3600);

      // Log alert
      console.warn('Location Search Alert:', {
        id: alertWithId.id,
        type: alert.type,
        severity: alert.severity,
        title: alert.title,
        description: alert.description,
      });

      // In production, this would integrate with alerting systems like:
      // - PagerDuty
      // - Slack notifications
      // - Email alerts
      // - DataDog alerts
      await this.sendToExternalAlertingSystems(alertWithId);
    } catch (error) {
      console.error('Failed to create alert:', error);
    }
  }

  /**
   * Send alert to external systems
   */
  private async sendToExternalAlertingSystems(alert: LocationAlert): Promise<void> {
    // This would integrate with your alerting infrastructure
    // For now, we'll just log the alert

    if (alert.severity === 'high' || alert.severity === 'critical') {
      // High severity alerts would trigger immediate notifications
      console.error('HIGH SEVERITY ALERT:', alert);
    }

    // Could integrate with:
    // - Webhook to Slack
    // - PagerDuty API
    // - Email service
    // - Push notifications
    // - CloudWatch alarms
    // - DataDog alerts
  }

  /**
   * Get active alerts
   */
  async getActiveAlerts(): Promise<LocationAlert[]> {
    try {
      if (!redisClient.isReady) return [];

      const alertData = await redisClient.hgetall(`${this.ALERTS_PREFIX}:active`);

      return Object.values(alertData)
        .map(data => JSON.parse(data) as LocationAlert)
        .filter(alert => !alert.resolved)
        .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime());
    } catch (error) {
      console.error('Failed to get active alerts:', error);
      return [];
    }
  }

  /**
   * Resolve an alert
   */
  async resolveAlert(alertId: string): Promise<boolean> {
    try {
      if (!redisClient.isReady) return false;

      const alertData = await redisClient.hget(`${this.ALERTS_PREFIX}:active`, alertId);
      if (!alertData) return false;

      const alert: LocationAlert = JSON.parse(alertData);
      alert.resolved = true;

      await redisClient.hset(`${this.ALERTS_PREFIX}:active`, alertId, JSON.stringify(alert));

      return true;
    } catch (error) {
      console.error('Failed to resolve alert:', error);
      return false;
    }
  }

  /**
   * Get empty metrics structure
   */
  private getEmptyMetrics(): LocationSearchMetrics {
    return {
      totalSearches: 0,
      averageExecutionTime: 0,
      cacheHitRate: 0,
      errorRate: 0,
      performanceDistribution: {
        excellent: 0,
        good: 0,
        acceptable: 0,
        poor: 0,
      },
      popularSearchAreas: [],
    };
  }
}

export const locationMonitoringService = new LocationMonitoringService();

import { redisClient } from '../config/redis.js';
import { BaseRepository } from '../repositories/BaseRepository.js';
import { Business } from '../types/Business.js';

export interface LocationSearchQuery {
  lat: number;
  lng: number;
  radius?: number; // kilometers, default 25
  category?: string[];
  search?: string;
  page?: number;
  limit?: number;
  sortBy?: 'distance' | 'rating' | 'newest';
  priceRange?: [number, number];
  amenities?: string[];
  isOpen?: boolean; // filter by current operating hours
}

export interface LocationSearchResult {
  businesses: EnhancedBusinessResult[];
  totalCount: number;
  searchRadius: number;
  searchCenter: { lat: number; lng: number };
  executionTimeMs: number;
  cacheHit: boolean;
}

export interface EnhancedBusinessResult extends Business {
  distance: number; // kilometers
  bearing?: number; // degrees from north
  estimatedTravelTime?: number; // minutes
  isCurrentlyOpen?: boolean;
  popularTimes?: { [hour: string]: number }; // 0-100 popularity score
}

export class LocationSearchService extends BaseRepository<Business> {
  private readonly CACHE_TTL = 300; // 5 minutes
  private readonly CACHE_PREFIX = 'location_search';
  private readonly MAX_RADIUS = 100; // Maximum search radius in km
  private readonly DEFAULT_RADIUS = 25;
  private readonly DEFAULT_LIMIT = 10;

  constructor() {
    super('businesses');
  }

  /**
   * High-performance location-based business search with multi-layer caching
   */
  async searchByLocation(query: LocationSearchQuery): Promise<LocationSearchResult> {
    const startTime = Date.now();

    // Validate and normalize query parameters
    const normalizedQuery = this.normalizeQuery(query);

    // Generate cache key based on query parameters
    const cacheKey = this.generateCacheKey(normalizedQuery);

    try {
      // Try cache first
      const cachedResult = await this.getCachedResult(cacheKey);
      if (cachedResult) {
        return {
          ...cachedResult,
          executionTimeMs: Date.now() - startTime,
          cacheHit: true,
        };
      }

      // Execute spatial search with PostGIS
      const searchResult = await this.executeLocationSearch(normalizedQuery);

      // Enhance results with additional data
      const enhancedResult = await this.enhanceSearchResults(searchResult, normalizedQuery);

      // Cache the result
      await this.cacheResult(cacheKey, enhancedResult);

      return {
        ...enhancedResult,
        executionTimeMs: Date.now() - startTime,
        cacheHit: false,
      };
    } catch (error) {
      console.error('Location search error:', {
        query: normalizedQuery,
        error: error instanceof Error ? error.message : 'Unknown error',
        executionTime: Date.now() - startTime,
      });
      throw error;
    }
  }

  /**
   * Get nearby businesses with distance-optimized queries
   */
  private async executeLocationSearch(query: LocationSearchQuery): Promise<{
    businesses: EnhancedBusinessResult[];
    totalCount: number;
  }> {
    const { lat, lng, radius, category, search, page, limit } = query;
    const offset = ((page || 1) - 1) * (limit || this.DEFAULT_LIMIT);

    // Use the optimized PostGIS search function
    const searchSql = `
      SELECT * FROM search_businesses_by_location(
        $1::FLOAT, $2::FLOAT, $3::FLOAT, $4::TEXT[], $5::TEXT, $6::INTEGER, $7::INTEGER
      )
    `;

    const countSql = `
      SELECT count_businesses_by_location(
        $1::FLOAT, $2::FLOAT, $3::FLOAT, $4::TEXT[], $5::TEXT
      ) as total_count
    `;

    const searchParams = [
      lat,
      lng,
      radius || this.DEFAULT_RADIUS,
      category || null,
      search || null,
      limit || this.DEFAULT_LIMIT,
      offset,
    ];

    const countParams = searchParams.slice(0, 5); // Remove limit and offset

    // Execute queries in parallel
    const [businessResult, countResult] = await Promise.all([
      this.query(searchSql, searchParams),
      this.query(countSql, countParams),
    ]);

    return {
      businesses: businessResult.rows.map(row => ({
        ...row,
        distance: parseFloat(row.distance_km),
      })),
      totalCount: parseInt(countResult.rows[0].total_count),
    };
  }

  /**
   * Enhance search results with additional computed data
   */
  private async enhanceSearchResults(
    result: { businesses: EnhancedBusinessResult[]; totalCount: number },
    query: LocationSearchQuery
  ): Promise<Omit<LocationSearchResult, 'executionTimeMs' | 'cacheHit'>> {
    const { businesses, totalCount } = result;
    const { lat, lng, radius } = query;

    // Add bearing calculations and business hours checks
    const enhancedBusinesses = await Promise.all(
      businesses.map(async (business) => {
        const enhanced: EnhancedBusinessResult = {
          ...business,
          bearing: this.calculateBearing(lat, lng, business.location.coordinates.lat, business.location.coordinates.lng),
          isCurrentlyOpen: this.isBusinessCurrentlyOpen(business.hours),
          estimatedTravelTime: this.estimateTravelTime(business.distance),
        };

        return enhanced;
      })
    );

    return {
      businesses: enhancedBusinesses,
      totalCount,
      searchRadius: radius || this.DEFAULT_RADIUS,
      searchCenter: { lat, lng },
    };
  }

  /**
   * Intelligent caching with hierarchical keys
   */
  private generateCacheKey(query: LocationSearchQuery): string {
    const { lat, lng, radius, category, search, page, limit, sortBy } = query;

    // Round coordinates to 4 decimal places (~11m precision) for better cache hits
    const roundedLat = Math.round(lat * 10000) / 10000;
    const roundedLng = Math.round(lng * 10000) / 10000;

    const keyParts = [
      this.CACHE_PREFIX,
      `${roundedLat},${roundedLng}`,
      `r${radius || this.DEFAULT_RADIUS}`,
      category ? `c${category.sort().join(',')}` : 'c_all',
      search ? `s${search.toLowerCase().replace(/\s+/g, '_')}` : 's_all',
      `p${page || 1}`,
      `l${limit || this.DEFAULT_LIMIT}`,
      `sort${sortBy || 'distance'}`,
    ];

    return keyParts.join(':');
  }

  /**
   * Cache result with intelligent TTL based on data freshness
   */
  private async cacheResult(
    cacheKey: string,
    result: Omit<LocationSearchResult, 'executionTimeMs' | 'cacheHit'>
  ): Promise<void> {
    try {
      if (!redisClient.isReady) {
        console.warn('Redis not available, skipping cache');
        return;
      }

      // Dynamic TTL based on result density
      const dynamicTTL = this.calculateDynamicTTL(result.businesses.length, result.totalCount);

      await redisClient.setEx(
        cacheKey,
        dynamicTTL,
        JSON.stringify(result)
      );

      // Create geographic cache clusters for nearby queries
      await this.updateGeographicCache(result.searchCenter, result.businesses);
    } catch (error) {
      console.error('Cache write error:', error);
      // Continue without caching - don't break the search
    }
  }

  /**
   * Retrieve cached result with fallback handling
   */
  private async getCachedResult(cacheKey: string): Promise<Omit<LocationSearchResult, 'executionTimeMs' | 'cacheHit'> | null> {
    try {
      if (!redisClient.isReady) {
        return null;
      }

      const cached = await redisClient.get(cacheKey);
      return cached ? JSON.parse(cached) : null;
    } catch (error) {
      console.error('Cache read error:', error);
      return null;
    }
  }

  /**
   * Update geographic clusters for related location searches
   */
  private async updateGeographicCache(
    center: { lat: number; lng: number },
    businesses: EnhancedBusinessResult[]
  ): Promise<void> {
    try {
      // Create a geographic cluster key for nearby searches
      const clusterKey = `${this.CACHE_PREFIX}:cluster:${Math.round(center.lat * 100)}:${Math.round(center.lng * 100)}`;

      const clusterData = {
        center,
        businessIds: businesses.map(b => b.id),
        timestamp: Date.now(),
      };

      await redisClient.setEx(clusterKey, 600, JSON.stringify(clusterData)); // 10 minutes
    } catch (error) {
      console.error('Geographic cache update error:', error);
    }
  }

  /**
   * Calculate dynamic TTL based on search result density
   */
  private calculateDynamicTTL(resultCount: number, totalCount: number): number {
    // Higher density areas change less frequently, can cache longer
    const density = resultCount / Math.max(totalCount, 1);

    if (density > 0.5) {
      return 600; // 10 minutes for high-density areas
    } else if (density > 0.2) {
      return 300; // 5 minutes for medium-density areas
    } else {
      return 120; // 2 minutes for sparse areas
    }
  }

  /**
   * Normalize and validate query parameters
   */
  private normalizeQuery(query: LocationSearchQuery): LocationSearchQuery {
    const { lat, lng, radius, limit, page } = query;

    // Validate coordinates
    if (!this.isValidCoordinate(lat, lng)) {
      throw new Error('Invalid coordinates provided');
    }

    return {
      ...query,
      radius: Math.min(radius || this.DEFAULT_RADIUS, this.MAX_RADIUS),
      limit: Math.min(limit || this.DEFAULT_LIMIT, 50), // Max 50 results per page
      page: Math.max(page || 1, 1),
    };
  }

  /**
   * Coordinate validation
   */
  private isValidCoordinate(lat: number, lng: number): boolean {
    return (
      typeof lat === 'number' && 
      typeof lng === 'number' &&
      lat >= -90 && lat <= 90 &&
      lng >= -180 && lng <= 180 &&
      !isNaN(lat) && !isNaN(lng)
    );
  }

  /**
   * Calculate bearing between two points
   */
  private calculateBearing(lat1: number, lng1: number, lat2: number, lng2: number): number {
    const dLng = ((lng2 - lng1) * Math.PI) / 180;
    const lat1Rad = (lat1 * Math.PI) / 180;
    const lat2Rad = (lat2 * Math.PI) / 180;

    const y = Math.sin(dLng) * Math.cos(lat2Rad);
    const x = Math.cos(lat1Rad) * Math.sin(lat2Rad) - Math.sin(lat1Rad) * Math.cos(lat2Rad) * Math.cos(dLng);

    const bearing = (Math.atan2(y, x) * 180) / Math.PI;
    return (bearing + 360) % 360;
  }

  /**
   * Check if business is currently open
   */
  private isBusinessCurrentlyOpen(hours: any): boolean {
    if (!hours || typeof hours !== 'object') return false;

    const now = new Date();
    const currentDay = now.toLocaleLowerCase()
      .slice(0, 3); // 'mon', 'tue', etc.
    const currentTime = now.getHours() * 100 + now.getMinutes();

    const todayHours = hours[currentDay];
    if (!todayHours || todayHours.closed) return false;

    const openTime = this.parseTime(todayHours.open);
    const closeTime = this.parseTime(todayHours.close);

    if (closeTime < openTime) {
      // Handles overnight hours (e.g., 10PM - 2AM)
      return currentTime >= openTime || currentTime <= closeTime;
    }

    return currentTime >= openTime && currentTime <= closeTime;
  }

  /**
   * Parse time string to numeric format (HHMM)
   */
  private parseTime(timeStr: string): number {
    if (!timeStr) return 0;
    const [hours, minutes] = timeStr.split(':').map(n => parseInt(n, 10));
    return hours * 100 + (minutes || 0);
  }

  /**
   * Estimate travel time based on distance
   */
  private estimateTravelTime(distanceKm: number): number {
    // Simple estimation: average 30 km/h in urban areas
    return Math.round((distanceKm / 30) * 60);
  }

  /**
   * Get categories available in a specific location
   */
  async getCategoriesInLocation(lat: number, lng: number, radius: number): Promise<string[]> {
    const cacheKey = `${this.CACHE_PREFIX}:categories:${Math.round(lat * 10000)}:${Math.round(lng * 10000)}:r${radius}`;

    try {
      // Try cache first
      if (redisClient.isReady) {
        const cached = await redisClient.get(cacheKey);
        if (cached) {
          return JSON.parse(cached);
        }
      }

      // Query categories within the specified location
      const query = `
        SELECT DISTINCT unnest(categories) as category
        FROM businesses
        WHERE is_active = true
          AND location_point IS NOT NULL
          AND ST_DWithin(
            ST_SetSRID(ST_MakePoint($2, $1), 4326)::geography,
            location_point::geography,
            $3 * 1000
          )
        ORDER BY category
      `;

      const result = await this.query(query, [lat, lng, radius]);
      const categories = result.rows.map(row => row.category);

      // Cache for 1 hour
      if (redisClient.isReady) {
        await redisClient.setEx(cacheKey, 3600, JSON.stringify(categories));
      }

      return categories;
    } catch (error) {
      console.error('Get categories in location error:', error);
      throw error;
    }
  }

  /**
   * Get popular areas with business density analysis
   */
  async getPopularAreas(lat: number, lng: number, radius: number): Promise<Array<{
    center: { lat: number; lng: number };
    businessCount: number;
    averageRating: number;
    topCategories: string[];
    name?: string;
  }>> {
    const cacheKey = `${this.CACHE_PREFIX}:popular:${Math.round(lat * 100)}:${Math.round(lng * 100)}:r${radius}`;

    try {
      // Try cache first
      if (redisClient.isReady) {
        const cached = await redisClient.get(cacheKey);
        if (cached) {
          return JSON.parse(cached);
        }
      }

      // Grid-based density analysis
      const gridSize = 0.01; // Approximately 1km grid cells
      const query = `
        WITH business_grid AS (
          SELECT 
            FLOOR(ST_Y(location_point) / $4) * $4 + $4/2 as grid_lat,
            FLOOR(ST_X(location_point) / $4) * $4 + $4/2 as grid_lng,
            COUNT(*) as business_count,
            AVG(COALESCE((SELECT AVG(rating) FROM reviews WHERE business_id = businesses.id), 4.0)) as avg_rating,
            array_agg(DISTINCT categories[1]) as top_categories
          FROM businesses
          WHERE is_active = true
            AND location_point IS NOT NULL
            AND ST_DWithin(
              ST_SetSRID(ST_MakePoint($2, $1), 4326)::geography,
              location_point::geography,
              $3 * 1000
            )
          GROUP BY grid_lat, grid_lng
          HAVING COUNT(*) >= 3
        )
        SELECT 
          grid_lat, grid_lng, business_count, 
          ROUND(avg_rating::numeric, 2) as avg_rating,
          (SELECT array_agg(cat) FROM unnest(top_categories) cat WHERE cat IS NOT NULL LIMIT 3) as top_categories
        FROM business_grid
        ORDER BY business_count DESC, avg_rating DESC
        LIMIT 10
      `;

      const result = await this.query(query, [lat, lng, radius, gridSize]);

      const popularAreas = result.rows.map(row => ({
        center: { 
          lat: parseFloat(row.grid_lat), 
          lng: parseFloat(row.grid_lng) 
        },
        businessCount: parseInt(row.business_count),
        averageRating: parseFloat(row.avg_rating),
        topCategories: row.top_categories || [],
        name: this.generateAreaName(parseFloat(row.grid_lat), parseFloat(row.grid_lng))
      }));

      // Cache for 30 minutes
      if (redisClient.isReady) {
        await redisClient.setEx(cacheKey, 1800, JSON.stringify(popularAreas));
      }

      return popularAreas;
    } catch (error) {
      console.error('Get popular areas error:', error);
      throw error;
    }
  }

  /**
   * Generate area name based on coordinates (basic implementation)
   */
  private generateAreaName(lat: number, lng: number): string {
    // This could be enhanced with reverse geocoding
    return `Area ${lat.toFixed(3)}, ${lng.toFixed(3)}`;
  }

  /**
   * Analyze search performance and cache efficiency
   */
  async getSearchAnalytics(): Promise<{
    totalSearches: number;
    cacheHitRate: number;
    averageExecutionTime: number;
    popularSearchAreas: Array<{ lat: number; lng: number; count: number }>;
  }> {
    try {
      if (!redisClient.isReady) {
        return {
          totalSearches: 0,
          cacheHitRate: 0,
          averageExecutionTime: 0,
          popularSearchAreas: [],
        };
      }

      // Get analytics from Redis (implementation would depend on tracking setup)
      const analytics = {
        totalSearches: 0,
        cacheHitRate: 0.85, // Example value
        averageExecutionTime: 150, // Example value in ms
        popularSearchAreas: [],
      };

      return analytics;
    } catch (error) {
      console.error('Get search analytics error:', error);
      throw error;
    }
  }

  /**
   * Invalidate location-based cache when businesses are updated
   */
  async invalidateLocationCache(businessId?: string, coordinates?: { lat: number; lng: number }): Promise<void> {
    try {
      if (!redisClient.isReady) return;

      if (coordinates) {
        // Invalidate specific geographic area
        const clusterKey = `${this.CACHE_PREFIX}:cluster:${Math.round(coordinates.lat * 100)}:${Math.round(coordinates.lng * 100)}`;
        await redisClient.del(clusterKey);

        // Invalidate categories cache
        const categoriesKey = `${this.CACHE_PREFIX}:categories:${Math.round(coordinates.lat * 10000)}:${Math.round(coordinates.lng * 10000)}:*`;
        const categoryKeys = await redisClient.keys(categoriesKey);
        if (categoryKeys.length > 0) {
          await redisClient.del(...categoryKeys);
        }

        // Invalidate popular areas cache
        const popularKey = `${this.CACHE_PREFIX}:popular:${Math.round(coordinates.lat * 100)}:${Math.round(coordinates.lng * 100)}:*`;
        const popularKeys = await redisClient.keys(popularKey);
        if (popularKeys.length > 0) {
          await redisClient.del(...popularKeys);
        }
      }

      // For broader invalidation, use pattern matching
      if (businessId) {
        // This would require a more sophisticated cache tagging system
        // For now, we'll use expiration-based invalidation
        console.log(`Location cache invalidation requested for business: ${businessId}`);
      }
    } catch (error) {
      console.error('Cache invalidation error:', error);
    }
  }
}

