      expect(response.body.error).toBe('User already exists');
      expect(cognitoServiceMock.registerUser).not.toHaveBeenCalled();
    });
  });

  describe('POST /auth/login', () => {
    const app = createTestApp(loginHandler);

    const validCredentials: LoginRequest = {
      email: 'test@example.com',
      password: 'Test123!',
    };

    it('should successfully login user', async () => {
      cognitoServiceMock.loginUser.mockResolvedValue({
        accessToken: 'access-token',
        refreshToken: 'refresh-token',
      });

      const mockPool = pool as any;
      mockPool.query
        .mockResolvedValueOnce({
          // Get user
          rows: [
            {
              id: 'user-123',
              email: 'test@example.com',
              role: 'consumer',
              profile: { firstName: 'John', lastName: 'Doe' },
              is_email_verified: true,
              created_at: new Date(),
              updated_at: new Date(),
              last_login_at: null,
            },
          ],
        })
        .mockResolvedValueOnce({}); // Update last login

      const response = await request(app).post('/').send(validCredentials).expect(200);

      expect(response.body).toHaveProperty('token');
      expect(response.body).toHaveProperty('refreshToken');
      expect(response.body).toHaveProperty('user');
      expect(cognitoServiceMock.loginUser).toHaveBeenCalledWith('test@example.com', 'Test123!');
    });

    it('should reject login with invalid credentials', async () => {
      cognitoServiceMock.loginUser.mockRejectedValue(new Error('Invalid credentials'));

      (AccountLockout as any).recordFailedAttempt.mockResolvedValue({
        attempts: 1,
        isLocked: false,
      });

      const response = await request(app).post('/').send(validCredentials).expect(401);

      expect(response.body.error).toBe('Invalid credentials');
      expect((AccountLockout as any).recordFailedAttempt).toHaveBeenCalledWith('test@example.com');
    });

    it('should handle account lockout', async () => {
      (AccountLockout as any).isAccountLocked.mockResolvedValue({
        isLocked: true,
        lockoutExpires: new Date(Date.now() + 30 * 60 * 1000),
      });

      const response = await request(app).post('/').send(validCredentials).expect(423);

      expect(response.body.error).toBe('Account temporarily locked');
      expect(cognitoServiceMock.loginUser).not.toHaveBeenCalled();
    });

    it('should reject login with missing fields', async () => {
      const response = await request(app).post('/').send({ email: 'test@example.com' }).expect(400);

      expect(response.body).toHaveProperty('error');
      expect(cognitoServiceMock.loginUser).not.toHaveBeenCalled();
    });
  });

  describe('POST /auth/refresh', () => {
    const app = createTestApp(refreshHandler);

    it('should successfully refresh token', async () => {
      cognitoServiceMock.refreshToken.mockResolvedValue({
        accessToken: 'new-access-token',
        idToken: 'new-id-token',
      });

      const response = await request(app)
        .post('/')
        .send({ refreshToken: 'valid-refresh-token' })
        .expect(200);

      expect(response.body).toHaveProperty('token');
      expect(response.body).toHaveProperty('idToken');
      expect(cognitoServiceMock.refreshToken).toHaveBeenCalledWith('valid-refresh-token');
    });

    it('should reject invalid refresh token', async () => {
      cognitoServiceMock.refreshToken.mockRejectedValue(new Error('Invalid refresh token'));

      const response = await request(app)
        .post('/')
        .send({ refreshToken: 'invalid-token' })
        .expect(401);

      expect(response.body.error).toBe('Invalid refresh token');
    });

    it('should reject missing refresh token', async () => {
      const response = await request(app).post('/').send({}).expect(400);

      expect(response.body).toHaveProperty('error');
      expect(cognitoServiceMock.refreshToken).not.toHaveBeenCalled();
    });
  });

  describe('POST /auth/logout', () => {
    const app = createTestApp(logoutHandler);

    it('should successfully logout user', async () => {
      // Note: In real implementation, we'd need to properly mock the middleware chain
      const response = await request(app)
        .post('/')
        .set('Authorization', 'Bearer valid-token')
        .expect(200);

      expect(response.body.message).toBe('Logged out successfully');
    });

    it('should reject logout without authentication', async () => {
      const response = await request(app).post('/').expect(401);

      expect(response.body).toHaveProperty('error');
    });
  });
});

import { afterAll, beforeAll, beforeEach, describe, expect, test } from '@jest/globals';
import { Pool } from 'pg';
import request from 'supertest';
import { app } from '../../app.js';
import { locationSearchService } from '../../services/locationSearchService.js';
import { redisClient } from '../../config/redis.js';
import { config } from '../../config/database.js';

describe('Location Search Integration Tests', () => {
  let pool: Pool;
  let testBusinessIds: string[] = [];

  beforeAll(async () => {
    pool = new Pool({
      ...config,
      database: `${config.database  }_test`,
    });
  });

  afterAll(async () => {
    // Cleanup test data
    if (testBusinessIds.length > 0) {
      await pool.query(
        `DELETE FROM businesses WHERE id = ANY($1)`,
        [testBusinessIds]
      );
    }
    await pool.end();
  });

  beforeEach(async () => {
    // Clear Redis cache before each test
    if (redisClient.isReady) {
      await redisClient.flushall();
    }
  });

  describe('End-to-End Location Search API', () => {
    beforeEach(async () => {
      // Insert comprehensive test data
      const testBusinesses = [
        {
          name: 'Downtown Restaurant',
          lat: 40.7128,
          lng: -74.0060,
          category: 'restaurant',
          description: 'Great downtown dining'
        },
        {
          name: 'Midtown Coffee Shop',
          lat: 40.7549,
          lng: -73.9840,
          category: 'coffee',
          description: 'Best coffee in midtown'
        },
        {
          name: 'Central Park Fitness',
          lat: 40.7829,
          lng: -73.9654,
          category: 'fitness',
          description: 'Fitness center near the park'
        },
        {
          name: 'Brooklyn Boutique',
          lat: 40.6782,
          lng: -73.9442,
          category: 'retail',
          description: 'Trendy brooklyn shopping'
        },
        {
          name: 'Queens Auto Service',
          lat: 40.7282,
          lng: -73.7949,
          category: 'automotive',
          description: 'Reliable auto repairs'
        }
      ];

      testBusinessIds = [];
      for (const business of testBusinesses) {
        const location = {
          address: '123 Test St',
          city: 'New York',
          state: 'NY',
          zipCode: '10001',
          coordinates: { lat: business.lat, lng: business.lng }
        };

        const result = await pool.query(`
          INSERT INTO businesses (
            id, owner_id, name, description, location, categories, hours, contact, is_active
          ) VALUES (
            gen_random_uuid(), gen_random_uuid(), $1, $2, $3, $4, $5, $6, true
          ) RETURNING id
        `, [
          business.name,
          business.description,
          JSON.stringify(location),
          [business.category],
          JSON.stringify({}),
          JSON.stringify({})
        ]);

        testBusinessIds.push(result.rows[0].id);
      }
    });

    test('should perform location search with sub-1s performance', async () => {
      const startTime = Date.now();

      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 25,
          limit: 10
        })
        .expect(200);

      const endTime = Date.now();
      const executionTime = endTime - startTime;

      // Performance requirement: <1 second
      expect(executionTime).toBeLessThan(1000);

      expect(response.body.success).toBe(true);
      expect(response.body.data.businesses).toBeInstanceOf(Array);
      expect(response.body.data.searchMetadata.executionTimeMs).toBeLessThan(1000);
      expect(response.body.data.pagination).toBeDefined();
    });

    test('should return businesses sorted by distance', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128, // Downtown Manhattan
          lng: -74.0060,
          radius: 50,
          limit: 5
        })
        .expect(200);

      const businesses = response.body.data.businesses;
      expect(businesses.length).toBeGreaterThan(0);

      // Verify distances are in ascending order
      for (let i = 1; i < businesses.length; i++) {
        expect(businesses[i].distance).toBeGreaterThanOrEqual(businesses[i - 1].distance);
      }

      // Verify the closest business is Downtown Restaurant
      expect(businesses[0].name).toBe('Downtown Restaurant');
    });

    test('should filter by category correctly', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 50,
          category: 'restaurant'
        })
        .expect(200);

      const businesses = response.body.data.businesses;
      expect(businesses.length).toBeGreaterThan(0);

      // All businesses should be restaurants
      businesses.forEach((business: any) => {
        expect(business.categories).toContain('restaurant');
      });
    });

    test('should handle text search within location', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7549,
          lng: -73.9840,
          radius: 50,
          search: 'coffee'
        })
        .expect(200);

      const businesses = response.body.data.businesses;
      expect(businesses.length).toBeGreaterThan(0);

      // Should find the coffee shop
      const coffeeShop = businesses.find((b: any) => b.name === 'Midtown Coffee Shop');
      expect(coffeeShop).toBeDefined();
    });

    test('should respect radius filtering', async () => {
      // Small radius should find fewer businesses
      const smallRadiusResponse = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 5 // 5km radius
        })
        .expect(200);

      // Large radius should find more businesses
      const largeRadiusResponse = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 50 // 50km radius
        })
        .expect(200);

      expect(largeRadiusResponse.body.data.businesses.length)
        .toBeGreaterThanOrEqual(smallRadiusResponse.body.data.businesses.length);
    });

    test('should handle pagination correctly', async () => {
      // First page
      const page1Response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 50,
          page: 1,
          limit: 2
        })
        .expect(200);

      // Second page
      const page2Response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 50,
          page: 2,
          limit: 2
        })
        .expect(200);

      const page1Businesses = page1Response.body.data.businesses;
      const page2Businesses = page2Response.body.data.businesses;

      expect(page1Businesses.length).toBeLessThanOrEqual(2);
      expect(page2Businesses.length).toBeGreaterThanOrEqual(0);

      // Verify pagination metadata
      expect(page1Response.body.data.pagination.page).toBe(1);
      expect(page2Response.body.data.pagination.page).toBe(2);
    });
  });

  describe('Cache Performance and Behavior', () => {
    test('should cache search results and show cache hits', async () => {
      const searchQuery = {
        lat: 40.7128,
        lng: -74.0060,
        radius: 25,
        limit: 10
      };

      // First request (cache miss)
      const response1 = await request(app)
        .get('/api/businesses/search/location')
        .query(searchQuery)
        .expect(200);

      expect(response1.body.data.searchMetadata.cacheHit).toBe(false);
      expect(response1.headers['x-cache']).toBe('MISS');

      // Second request (should be cache hit)
      const response2 = await request(app)
        .get('/api/businesses/search/location')
        .query(searchQuery)
        .expect(200);

      expect(response2.body.data.searchMetadata.cacheHit).toBe(true);
      expect(response2.headers['x-cache']).toBe('HIT');

      // Cache hit should be faster
      expect(response2.body.data.searchMetadata.executionTimeMs)
        .toBeLessThan(response1.body.data.searchMetadata.executionTimeMs);
    });

    test('should handle cache invalidation correctly', async () => {
      const searchQuery = {
        lat: 40.7128,
        lng: -74.0060,
        radius: 25
      };

      // First request
      await request(app)
        .get('/api/businesses/search/location')
        .query(searchQuery)
        .expect(200);

      // Invalidate cache for this location
      await locationSearchService.invalidateLocationCache(
        undefined,
        { lat: searchQuery.lat, lng: searchQuery.lng }
      );

      // Next request should be cache miss
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query(searchQuery)
        .expect(200);

      expect(response.body.data.searchMetadata.cacheHit).toBe(false);
    });
  });

  describe('Categories in Location API', () => {
    test('should return available categories in location', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location/categories')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 50
        })
        .expect(200);

      expect(response.body.success).toBe(true);
      expect(response.body.data.categories).toBeInstanceOf(Array);
      expect(response.body.data.categories.length).toBeGreaterThan(0);

      // Should contain categories from our test data
      expect(response.body.data.categories).toContain('restaurant');
      expect(response.body.data.location).toEqual({
        lat: 40.7128,
        lng: -74.0060,
        radius: 50
      });
    });

    test('should cache categories results', async () => {
      const query = { lat: 40.7128, lng: -74.0060, radius: 25 };

      // First request (cache miss)
      const response1 = await request(app)
        .get('/api/businesses/search/location/categories')
        .query(query)
        .expect(200);

      // Second request should be faster (cache hit)
      const startTime = Date.now();
      const response2 = await request(app)
        .get('/api/businesses/search/location/categories')
        .query(query)
        .expect(200);
      const endTime = Date.now();

      expect(response2.body.data.categories).toEqual(response1.body.data.categories);
      expect(endTime - startTime).toBeLessThan(50); // Should be very fast
    });
  });

  describe('Popular Areas API', () => {
    test('should return popular business areas', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location/popular-areas')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 50
        })
        .expect(200);

      expect(response.body.success).toBe(true);
      expect(response.body.data.popularAreas).toBeInstanceOf(Array);
      expect(response.body.data.searchCenter).toBeDefined();

      if (response.body.data.popularAreas.length > 0) {
        const area = response.body.data.popularAreas[0];
        expect(area.center).toBeDefined();
        expect(area.center.lat).toBeTypeOf('number');
        expect(area.center.lng).toBeTypeOf('number');
        expect(area.businessCount).toBeTypeOf('number');
        expect(area.averageRating).toBeTypeOf('number');
        expect(area.topCategories).toBeInstanceOf(Array);
      }
    });
  });

  describe('Error Handling', () => {
    test('should validate required coordinates', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location')
        .expect(400);

      expect(response.body.success).toBe(false);
      expect(response.body.error).toContain('Invalid search parameters');
    });

    test('should validate coordinate ranges', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 999, // Invalid latitude
          lng: -74.0060,
          radius: 25
        })
        .expect(400);

      expect(response.body.success).toBe(false);
    });

    test('should handle invalid radius values', async () => {
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 999 // Too large radius
        })
        .expect(400);

      expect(response.body.success).toBe(false);
    });

    test('should handle database connection errors gracefully', async () => {
      // Temporarily close the database connection
      await pool.end();

      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 25
        })
        .expect(500);

      expect(response.body.success).toBe(false);
      expect(response.body.error).toBe('Location search failed');

      // Restore connection for cleanup
      pool = new Pool({
        ...config,
        database: `${config.database  }_test`,
      });
    });
  });

  describe('Security and Rate Limiting', () => {
    test('should handle malicious coordinate inputs', async () => {
      const maliciousInputs = [
        { lat: 'DROP TABLE businesses;', lng: -74.0060 },
        { lat: 40.7128, lng: '<script>alert("xss")</script>' },
        { lat: '40.7128; DELETE FROM businesses WHERE 1=1;--', lng: -74.0060 }
      ];

      for (const input of maliciousInputs) {
        const response = await request(app)
          .get('/api/businesses/search/location')
          .query({
            lat: input.lat,
            lng: input.lng,
            radius: 25
          });

        expect(response.status).toBeGreaterThanOrEqual(400);
        expect(response.body.success).toBe(false);
      }
    });

    test('should sanitize text search inputs', async () => {
      const maliciousSearch = '<script>alert("xss")</script>';

      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 25,
          search: maliciousSearch
        });

      // Should either succeed with sanitized input or fail with validation error
      if (response.status === 200) {
        // Verify no businesses match malicious script
        expect(response.body.data.businesses.length).toBe(0);
      } else {
        expect(response.status).toBe(400);
      }
    });
  });

  describe('Performance Benchmarks', () => {
    test('should handle high-volume concurrent requests', async () => {
      const concurrentRequests = 20;
      const requests = Array(concurrentRequests).fill(null).map((_, index) =>
        request(app)
          .get('/api/businesses/search/location')
          .query({
            lat: 40.7128 + (index * 0.001), // Slightly different locations
            lng: -74.0060 + (index * 0.001),
            radius: 25
          })
      );

      const startTime = Date.now();
      const responses = await Promise.all(requests);
      const endTime = Date.now();

      const totalTime = endTime - startTime;
      const avgTimePerRequest = totalTime / concurrentRequests;

      expect(totalTime).toBeLessThan(5000); // All requests under 5 seconds
      expect(avgTimePerRequest).toBeLessThan(1000); // Average under 1 second

      // All requests should succeed
      responses.forEach(response => {
        expect(response.status).toBe(200);
        expect(response.body.success).toBe(true);
      });
    });

    test('should maintain performance with large datasets', async () => {
      // This test would be more meaningful with a larger test dataset
      // For now, we'll test with current data
      const response = await request(app)
        .get('/api/businesses/search/location')
        .query({
          lat: 40.7128,
          lng: -74.0060,
          radius: 100, // Large radius
          limit: 50    // Large limit
        })
        .expect(200);

      expect(response.body.data.searchMetadata.executionTimeMs).toBeLessThan(1000);
      expect(response.body.data.businesses.length).toBeGreaterThanOrEqual(0);
    });
  });
});

import { NextFunction, Response } from 'express';
import {
  CognitoAuthenticatedRequest,
  authenticateCognito,
  requireAdmin,
  requireBusinessOwner,
  requireConsumer,
  requireRole,
} from '../../middleware/cognitoAuth';
import { CognitoJwtVerifier } from 'aws-jwt-verify';

// Mock aws-jwt-verify
jest.mock('aws-jwt-verify');

describe('CognitoAuth Middleware', () => {
  let mockReq: Partial<CognitoAuthenticatedRequest>;
  let mockRes: Partial<Response>;
  let mockNext: NextFunction;
  let mockVerifier: jest.Mocked<any>;

  beforeEach(() => {
    jest.clearAllMocks();

    mockReq = {
      headers: {},
    };

    mockRes = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
    };

    mockNext = jest.fn();

    // Mock the verifier
    mockVerifier = {
      verify: jest.fn(),
    };

    (CognitoJwtVerifier.create as jest.Mock).mockReturnValue(mockVerifier);
  });

  describe('authenticateCognito', () => {
    it('should authenticate valid token', async () => {
      const mockPayload = {
        sub: 'user-123',
        email: 'test@example.com',
        'custom:role': 'consumer',
        email_verified: true,
      };

      mockReq.headers!.authorization = 'Bearer valid-token';
      mockVerifier.verify.mockResolvedValue(mockPayload);

      await authenticateCognito(
        mockReq as CognitoAuthenticatedRequest,
        mockRes as Response,
        mockNext
      );

      expect(mockReq.user).toEqual({
        id: 'user-123',
        email: 'test@example.com',
        role: 'consumer',
        emailVerified: true,
      });
      expect(mockNext).toHaveBeenCalled();
      expect(mockRes.status).not.toHaveBeenCalled();
    });

    it('should reject request without authorization header', async () => {
      await authenticateCognito(
        mockReq as CognitoAuthenticatedRequest,
        mockRes as Response,
        mockNext
      );

      expect(mockRes.status).toHaveBeenCalledWith(401);
      expect(mockRes.json).toHaveBeenCalledWith({ error: 'Access token required' });
      expect(mockNext).not.toHaveBeenCalled();
    });

    it('should reject request with invalid token format', async () => {
      mockReq.headers!.authorization = 'Invalid token';

      await authenticateCognito(
        mockReq as CognitoAuthenticatedRequest,
        mockRes as Response,
        mockNext
      );

      expect(mockRes.status).toHaveBeenCalledWith(401);
      expect(mockRes.json).toHaveBeenCalledWith({ error: 'Access token required' });
      expect(mockNext).not.toHaveBeenCalled();
    });

    it('should reject expired or invalid token', async () => {
      mockReq.headers!.authorization = 'Bearer expired-token';
      mockVerifier.verify.mockRejectedValue(new Error('Token expired'));

      await authenticateCognito(
        mockReq as CognitoAuthenticatedRequest,
        mockRes as Response,
        mockNext
      );

      expect(mockRes.status).toHaveBeenCalledWith(403);
      expect(mockRes.json).toHaveBeenCalledWith({ error: 'Invalid or expired token' });
      expect(mockNext).not.toHaveBeenCalled();
    });

    it('should handle missing email in token', async () => {
      const mockPayload = {
        sub: 'user-123',
        'custom:role': 'consumer',
        email_verified: true,
      };

      mockReq.headers!.authorization = 'Bearer valid-token';
      mockVerifier.verify.mockResolvedValue(mockPayload);

      await authenticateCognito(
        mockReq as CognitoAuthenticatedRequest,
        mockRes as Response,
        mockNext
      );

      expect(mockReq.user?.email).toBe('');
      expect(mockNext).toHaveBeenCalled();
    });

    it('should handle missing role in token', async () => {
      const mockPayload = {
        sub: 'user-123',
        email: 'test@example.com',
        email_verified: true,
      };

      mockReq.headers!.authorization = 'Bearer valid-token';
      mockVerifier.verify.mockResolvedValue(mockPayload);

      await authenticateCognito(
        mockReq as CognitoAuthenticatedRequest,
        mockRes as Response,
        mockNext
      );

      expect(mockReq.user?.role).toBe('consumer');
      expect(mockNext).toHaveBeenCalled();
    });
  });

  describe('requireRole', () => {
    beforeEach(() => {
      mockReq.user = {
        id: 'user-123',
        email: 'test@example.com',
        role: 'consumer',
        emailVerified: true,
      };
    });

    it('should allow access with correct role', () => {
      const middleware = requireRole(['consumer', 'business_owner']);

      middleware(mockReq as CognitoAuthenticatedRequest, mockRes as Response, mockNext);

      expect(mockNext).toHaveBeenCalled();
      expect(mockRes.status).not.toHaveBeenCalled();
    });

    it('should deny access with incorrect role', () => {
      const middleware = requireRole(['admin']);

      middleware(mockReq as CognitoAuthenticatedRequest, mockRes as Response, mockNext);

      expect(mockRes.status).toHaveBeenCalledWith(403);
      expect(mockRes.json).toHaveBeenCalledWith({ error: 'Insufficient permissions' });
      expect(mockNext).not.toHaveBeenCalled();
    });

    it('should deny access without authentication', () => {
      mockReq.user = undefined;
      const middleware = requireRole(['consumer']);

      middleware(mockReq as CognitoAuthenticatedRequest, mockRes as Response, mockNext);

      expect(mockRes.status).toHaveBeenCalledWith(401);
      expect(mockRes.json).toHaveBeenCalledWith({ error: 'Authentication required' });
      expect(mockNext).not.toHaveBeenCalled();
    });
  });

  describe('convenience role middlewares', () => {
    beforeEach(() => {
      mockReq.user = {
        id: 'user-123',
        email: 'test@example.com',
        role: 'business_owner',
        emailVerified: true,
      };
    });

    it('requireConsumer should work for consumer role', () => {
      mockReq.user!.role = 'consumer';

      requireConsumer(mockReq as CognitoAuthenticatedRequest, mockRes as Response, mockNext);

      expect(mockNext).toHaveBeenCalled();
    });

    it('requireBusinessOwner should work for business_owner role', () => {
      requireBusinessOwner(mockReq as CognitoAuthenticatedRequest, mockRes as Response, mockNext);

      expect(mockNext).toHaveBeenCalled();
    });

    it('requireAdmin should deny non-admin access', () => {
      requireAdmin(mockReq as CognitoAuthenticatedRequest, mockRes as Response, mockNext);

      expect(mockRes.status).toHaveBeenCalledWith(403);
      expect(mockNext).not.toHaveBeenCalled();
    });
  });
});

import { NextFunction, Request, Response } from 'express';
import { AccountLockout, createRateLimit } from '../../middleware/rateLimiting';
import { createClient } from 'redis';

// Mock Redis client
jest.mock('redis');

describe('Rate Limiting Middleware', () => {
  let mockReq: Partial<Request>;
  let mockRes: Partial<Response>;
  let mockNext: NextFunction;
  let mockRedisClient: any;

  beforeEach(() => {
    jest.clearAllMocks();

    mockReq = {
      ip: '192.168.1.1',
      headers: {},
    };

    mockRes = {
      status: jest.fn().mockReturnThis(),
      json: jest.fn().mockReturnThis(),
      set: jest.fn().mockReturnThis(),
    };

    mockNext = jest.fn();

    // Mock Redis client methods
    mockRedisClient = {
      isOpen: true,
      connect: jest.fn(),
      zRemRangeByScore: jest.fn(),
      zCard: jest.fn(),
      zAdd: jest.fn(),
      expire: jest.fn(),
      zRemRangeByRank: jest.fn(),
      incr: jest.fn(),
      exists: jest.fn(),
      ttl: jest.fn(),
      setEx: jest.fn(),
      del: jest.fn(),
    };

    (createClient as jest.Mock).mockReturnValue(mockRedisClient);
  });

  describe('createRateLimit', () => {
    it('should allow request within rate limit', async () => {
      const rateLimit = createRateLimit({
        windowMs: 60000, // 1 minute
        maxRequests: 5,
      });

      mockRedisClient.zCard.mockResolvedValue(2); // Current request count

      await rateLimit(mockReq as Request, mockRes as Response, mockNext);

      expect(mockRedisClient.zRemRangeByScore).toHaveBeenCalled();
      expect(mockRedisClient.zCard).toHaveBeenCalled();
      expect(mockRedisClient.zAdd).toHaveBeenCalled();
      expect(mockRes.set).toHaveBeenCalledWith({
        'X-RateLimit-Limit': '5',
        'X-RateLimit-Remaining': '2',
        'X-RateLimit-Reset': expect.any(String),
      });
      expect(mockNext).toHaveBeenCalled();
      expect(mockRes.status).not.toHaveBeenCalled();
    });

    it('should block request when rate limit exceeded', async () => {
      const rateLimit = createRateLimit({
        windowMs: 60000,
        maxRequests: 5,
        message: 'Too many requests',
      });

      mockRedisClient.zCard.mockResolvedValue(5); // At rate limit

      await rateLimit(mockReq as Request, mockRes as Response, mockNext);

      expect(mockRes.status).toHaveBeenCalledWith(429);
      expect(mockRes.json).toHaveBeenCalledWith({
        error: 'Rate limit exceeded',
        message: 'Too many requests',
        retryAfter: 60,
      });
      expect(mockNext).not.toHaveBeenCalled();
    });

    it('should use custom key generator', async () => {
      const rateLimit = createRateLimit({
        windowMs: 60000,
        maxRequests: 5,
        keyGenerator: req => `user:${req.headers['user-id']}`,
      });

      mockReq.headers!['user-id'] = 'user123';
      mockRedisClient.zCard.mockResolvedValue(1);

      await rateLimit(mockReq as Request, mockRes as Response, mockNext);

      // Verify Redis operations used the custom key
      expect(mockRedisClient.zRemRangeByScore).toHaveBeenCalledWith(
        'rate_limit:user:user123',
        expect.any(Number),
        expect.any(Number)
      );
      expect(mockNext).toHaveBeenCalled();
    });

    it('should continue on Redis errors', async () => {
      const rateLimit = createRateLimit({
        windowMs: 60000,
        maxRequests: 5,
      });

      mockRedisClient.zRemRangeByScore.mockRejectedValue(new Error('Redis connection failed'));

      await rateLimit(mockReq as Request, mockRes as Response, mockNext);

      expect(mockNext).toHaveBeenCalled(); // Should not block on Redis failure
    });

    it('should handle skipSuccessfulRequests option', async () => {
      const rateLimit = createRateLimit({
        windowMs: 60000,
        maxRequests: 5,
        skipSuccessfulRequests: true,
      });

      mockRedisClient.zCard.mockResolvedValue(1);

      // Mock response status tracking
      const originalStatus = mockRes.status;
      mockRes.status = jest.fn(code => {
        return originalStatus?.call(mockRes, code);
      });

      await rateLimit(mockReq as Request, mockRes as Response, mockNext);

      // Simulate successful response
      jest.spyOn(mockRes, 'json' as any);
      mockRes.json!({ success: true });

      expect(mockNext).toHaveBeenCalled();
    });
  });

  describe('AccountLockout', () => {
    describe('recordFailedAttempt', () => {
      it('should record failed attempt and not lock account initially', async () => {
        mockRedisClient.exists.mockResolvedValue(0); // Not locked
        mockRedisClient.incr.mockResolvedValue(1); // First attempt

        const result = await AccountLockout.recordFailedAttempt('test@example.com');

        expect(result.attempts).toBe(1);
        expect(result.isLocked).toBe(false);
        expect(mockRedisClient.incr).toHaveBeenCalledWith('attempts:test@example.com');
        expect(mockRedisClient.expire).toHaveBeenCalled();
      });

      it('should lock account after max attempts', async () => {
        mockRedisClient.exists.mockResolvedValue(0); // Not locked
        mockRedisClient.incr.mockResolvedValue(5); // Max attempts reached

        const result = await AccountLockout.recordFailedAttempt('test@example.com');

        expect(result.attempts).toBe(5);
        expect(result.isLocked).toBe(true);
        expect(result.lockoutExpires).toBeInstanceOf(Date);
        expect(mockRedisClient.setEx).toHaveBeenCalledWith(
          'lockout:test@example.com',
          1800,
          'locked'
        );
        expect(mockRedisClient.del).toHaveBeenCalledWith('attempts:test@example.com');
      });

      it('should return locked status if already locked', async () => {
        mockRedisClient.exists.mockResolvedValue(1); // Already locked
        mockRedisClient.ttl.mockResolvedValue(900); // 15 minutes remaining

        const result = await AccountLockout.recordFailedAttempt('test@example.com');

        expect(result.attempts).toBe(5);
        expect(result.isLocked).toBe(true);
        expect(result.lockoutExpires).toBeInstanceOf(Date);
        expect(mockRedisClient.incr).not.toHaveBeenCalled();
      });

      it('should handle Redis errors gracefully', async () => {
        mockRedisClient.exists.mockRejectedValue(new Error('Redis error'));

        const result = await AccountLockout.recordFailedAttempt('test@example.com');

        expect(result.attempts).toBe(0);
        expect(result.isLocked).toBe(false);
      });
    });

    describe('isAccountLocked', () => {
      it('should return locked status with expiration', async () => {
        mockRedisClient.exists.mockResolvedValue(1);
        mockRedisClient.ttl.mockResolvedValue(600); // 10 minutes remaining

        const result = await AccountLockout.isAccountLocked('test@example.com');

        expect(result.isLocked).toBe(true);
        expect(result.lockoutExpires).toBeInstanceOf(Date);
      });

      it('should return not locked status', async () => {
        mockRedisClient.exists.mockResolvedValue(0);

        const result = await AccountLockout.isAccountLocked('test@example.com');

        expect(result.isLocked).toBe(false);
        expect(result.lockoutExpires).toBeUndefined();
      });

      it('should handle Redis errors gracefully', async () => {
        mockRedisClient.exists.mockRejectedValue(new Error('Redis error'));

        const result = await AccountLockout.isAccountLocked('test@example.com');

        expect(result.isLocked).toBe(false);
      });
    });

    describe('clearFailedAttempts', () => {
      it('should clear attempts and lockout keys', async () => {
        await AccountLockout.clearFailedAttempts('test@example.com');

        expect(mockRedisClient.del).toHaveBeenCalledWith('attempts:test@example.com');
        expect(mockRedisClient.del).toHaveBeenCalledWith('lockout:test@example.com');
      });

      it('should handle Redis errors gracefully', async () => {
        mockRedisClient.del.mockRejectedValue(new Error('Redis error'));

        await expect(AccountLockout.clearFailedAttempts('test@example.com')).resolves.not.toThrow();
      });
    });

    describe('unlockAccount', () => {
      it('should unlock account manually', async () => {
        await AccountLockout.unlockAccount('test@example.com');

        expect(mockRedisClient.del).toHaveBeenCalledWith('attempts:test@example.com');
        expect(mockRedisClient.del).toHaveBeenCalledWith('lockout:test@example.com');
      });

      it('should throw error on Redis failure', async () => {
        mockRedisClient.del.mockRejectedValue(new Error('Redis error'));

        await expect(AccountLockout.unlockAccount('test@example.com')).rejects.toThrow(
          'Failed to unlock account'
        );
      });
    });
  });
});

import { Response } from 'express';

export interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  message?: string;
  error?: string;
  statusCode: number;
  timestamp: string;
}

export const successResponse = <T>(
  res: Response,
  statusCode: number = 200,
  data?: T,
  message?: string
): Response => {
  const response: ApiResponse<T> = {
    success: true,
    statusCode,
    timestamp: new Date().toISOString(),
  };

  if (data !== undefined) {
    response.data = data;
  }

  if (message) {
    response.message = message;
  }

  return res.status(statusCode).json(response);
};

export const errorResponse = (
  res: Response,
  statusCode: number = 500,
  error: string = 'Internal server error',
  details?: string[]
): Response => {
  const response: ApiResponse = {
    success: false,
    error,
    statusCode,
    timestamp: new Date().toISOString(),
  };

  if (details && details.length > 0) {
    response.details = details;
  }

  return res.status(statusCode).json(response);
};

export const paginatedResponse = <T>(
  res: Response,
  data: T[],
  totalCount: number,
  page: number = 1,
  limit: number = 10,
  statusCode: number = 200
): Response => {
  const totalPages = Math.ceil(totalCount / limit);
  const hasNextPage = page < totalPages;
  const hasPrevPage = page > 1;

  const response: ApiResponse<{
    items: T[];
    pagination: {
      totalCount: number;
      totalPages: number;
      currentPage: number;
      limit: number;
      hasNextPage: boolean;
      hasPrevPage: boolean;
    };
  }> = {
    success: true,
    data: {
      items: data,
      pagination: {
        totalCount,
        totalPages,
        currentPage: page,
        limit,
        hasNextPage,
        hasPrevPage,
      },
    },
    statusCode,
    timestamp: new Date().toISOString(),
  };

  return res.status(statusCode).json(response);
};

import { createClient } from 'redis';
import { config } from '../config/environment.js';

// Redis client for session management
const redisClient = createClient({
  url: config.redisUrl,
});

redisClient.on('error', (err) => {
  console.error('Redis Client Error:', err);
});

// Initialize Redis connection
export const initializeRedis = async () => {
  if (!redisClient.isOpen) {
    await redisClient.connect();
  }
  return redisClient;
};

export class SessionManager {
  private static readonly TOKEN_BLACKLIST_PREFIX = 'blacklist:token:';
  private static readonly USER_SESSION_PREFIX = 'session:user:';
  private static readonly DEFAULT_TTL = 24 * 60 * 60; // 24 hours in seconds

  /**
   * Blacklist a token (for logout functionality)
   */
  static async blacklistToken(tokenId: string, expirationTime?: number): Promise<void> {
    try {
      await initializeRedis();
      const key = `${this.TOKEN_BLACKLIST_PREFIX}${tokenId}`;
      const ttl = expirationTime ? Math.floor(expirationTime - Date.now() / 1000) : this.DEFAULT_TTL;

      if (ttl > 0) {
        await redisClient.setEx(key, ttl, 'blacklisted');
      }
    } catch (error) {
      console.error('Error blacklisting token:', error);
      throw new Error('Failed to blacklist token');
    }
  }

  /**
   * Check if a token is blacklisted
   */
  static async isTokenBlacklisted(tokenId: string): Promise<boolean> {
    try {
      await initializeRedis();
      const key = `${this.TOKEN_BLACKLIST_PREFIX}${tokenId}`;
      const result = await redisClient.get(key);
      return result !== null;
    } catch (error) {
      console.error('Error checking token blacklist:', error);
      return false; // Fail open - don't block valid tokens due to Redis issues
    }
  }

  /**
   * Store user session data
   */
  static async storeUserSession(userId: string, sessionData: {
    deviceInfo?: string;
    ipAddress?: string;
    lastActivity: Date;
    loginTime: Date;
  }): Promise<void> {
    try {
      await initializeRedis();
      const key = `${this.USER_SESSION_PREFIX}${userId}`;
      const data = {
        ...sessionData,
        lastActivity: sessionData.lastActivity.toISOString(),
        loginTime: sessionData.loginTime.toISOString(),
      };

      await redisClient.setEx(key, this.DEFAULT_TTL, JSON.stringify(data));
    } catch (error) {
      console.error('Error storing user session:', error);
      // Don't throw - session storage failure shouldn't block authentication
    }
  }

  /**
   * Get user session data
   */
  static async getUserSession(userId: string): Promise<{
    deviceInfo?: string;
    ipAddress?: string;
    lastActivity: Date;
    loginTime: Date;
  } | null> {
    try {
      await initializeRedis();
      const key = `${this.USER_SESSION_PREFIX}${userId}`;
      const data = await redisClient.get(key);

      if (!data) return null;

      const sessionData = JSON.parse(data);
      return {
        ...sessionData,
        lastActivity: new Date(sessionData.lastActivity),
        loginTime: new Date(sessionData.loginTime),
      };
    } catch (error) {
      console.error('Error getting user session:', error);
      return null;
    }
  }

  /**
   * Update user's last activity time
   */
  static async updateLastActivity(userId: string): Promise<void> {
    try {
      await initializeRedis();
      const key = `${this.USER_SESSION_PREFIX}${userId}`;
      const existingData = await redisClient.get(key);

      if (existingData) {
        const sessionData = JSON.parse(existingData);
        sessionData.lastActivity = new Date().toISOString();
        await redisClient.setEx(key, this.DEFAULT_TTL, JSON.stringify(sessionData));
      }
    } catch (error) {
      console.error('Error updating last activity:', error);
      // Don't throw - activity update failure shouldn't block requests
    }
  }

  /**
   * Clear user session (logout)
   */
  static async clearUserSession(userId: string): Promise<void> {
    try {
      await initializeRedis();
      const key = `${this.USER_SESSION_PREFIX}${userId}`;
      await redisClient.del(key);
    } catch (error) {
      console.error('Error clearing user session:', error);
      throw new Error('Failed to clear user session');
    }
  }

  /**
   * Clean up expired sessions (should be called periodically)
   */
  static async cleanupExpiredSessions(): Promise<void> {
    try {
      await initializeRedis();
      // Redis handles TTL expiration automatically, so this is mainly for logging
      console.log('Session cleanup completed');
    } catch (error) {
      console.error('Error during session cleanup:', error);
    }
  }
}

// Export Redis client for other utilities
export { redisClient };
